import React from 'react';
import { IonApp, IonRouterOutlet } from '@ionic/react';
import { IonReactHashRouter } from '@ionic/react-router';
import { Switch, Route } from 'react-router-dom';
import ActMatrixForm from './components/ActMatrixForm';
import AwayMovesDetail from './components/AwayMovesDetail.jsx';
import HooksRecordTable from './components/HooksRecordTable.jsx';
import { MatrixProvider } from './store/matrixStore';
import styles from './styles/App.module.css';
import './styles/PaperTheme.css';

/**
 * ‰∏ªÂ∫îÁî®ÁªÑ‰ª∂
 * 
 * ÂΩìÂâç‰ΩøÁî® Welcome ÁªÑ‰ª∂‰Ωú‰∏∫ÊºîÁ§∫È°µÈù¢
 * Âú®ÂÆûÈôÖÂºÄÂèë‰∏≠ÔºåËØ∑ÊõøÊç¢ Welcome ÁªÑ‰ª∂‰∏∫ÊÇ®ÁöÑÂ∫îÁî®ÂÜÖÂÆπ
 */
export default function App() {
    return (
        <MatrixProvider>
            <IonApp>
                <IonReactHashRouter>
                    <IonRouterOutlet>
                        <Switch>
                            <Route exact path="/">
                                <ActMatrixForm />
                            </Route>
                            <Route path="/away/:matrixId?">
                                <AwayMovesDetail />
                            </Route>
                            <Route path="/hooks/:matrixId">
                                <HooksRecordTable />
                            </Route>
                        </Switch>
                    </IonRouterOutlet>
                </IonReactHashRouter>
            </IonApp>
        </MatrixProvider>
    );
}

