                        </IonButton>
                    </IonButtons>
                    <IonTitle>ËøúÁ¶ªË°å‰∏∫ËØ¶ÊÉÖ</IonTitle>
                </IonToolbar>
            </IonHeader>
            <IonContent>
                {loading && (
                    <div style={{ display: 'flex', justifyContent: 'center', padding: 24 }}>
                        <IonSpinner name="crescent" />
                    </div>
                )}

                {!loading && items.length === 0 && (
                    <div style={{ padding: 24 }}>
                        <div style={{ color: '#8a837a', textAlign: 'center', marginBottom: 12 }}>ÊöÇÊó†ËøúÁ¶ªË°å‰∏∫</div>
                        <div style={{ display: 'flex', justifyContent: 'center' }}>
                            <IonButton 
                                onClick={() => setAddModalOpen(true)}
                                color="primary"
                                fill="solid"
                            >
                                <IonIcon icon={add} slot="start" /> Ê∑ªÂä†
                            </IonButton>
                        </div>
                    </div>
                )}

                {!loading && items.length > 0 && (
                    <div style={{ padding: '12px', overflowX: 'hidden' }}>
                        <div style={{
                            display: 'grid',
                            gridTemplateColumns: '20% 1fr 1fr 1fr',
                            gap: '8px',
                            alignItems: 'center',
                            padding: '8px 6px',
                            borderBottom: '1px solid var(--line-color)',
                            color: 'rgba(0,0,0,0.6)',
                            fontSize: 12,
                            fontWeight: 600
                        }}>
                            <div>ËøúÁ¶ªË°å‰∏∫</div>
                            <div style={{ textAlign: 'center', borderLeft: '1px dashed var(--line-color)', paddingLeft: 8 }}>Áü≠Êúü</div>
                            <div style={{ textAlign: 'center', borderLeft: '1px dashed var(--line-color)', paddingLeft: 8 }}>ÈïøÊúü</div>
                            <div style={{ textAlign: 'center', borderLeft: '1px dashed var(--line-color)', paddingLeft: 8 }}>ÈáçË¶Å</div>
                        </div>

                        {items.map((it) => {
                            const scores = it.scores || {};
                            const st = scores.shortTerm || {};
                            const lt = scores.longTerm || {};
                            const im = scores.importance || {};
                            return (
                                <div key={it.id} style={{
                                    display: 'grid',
                                    gridTemplateColumns: '20% 1fr 1fr 1fr',
                                    gap: '8px',
                                    alignItems: 'center',
                                    padding: '10px 6px',
                                    borderBottom: '1px dashed var(--line-color)'
                                }}>
                                    <div style={{ fontSize: 14, color: '#2b2b2b', overflowWrap: 'anywhere', wordWrap: 'break-word', whiteSpace: 'normal' }}>{it.content}</div>
                                    <div style={{ borderLeft: '1px dashed var(--line-color)', paddingLeft: 8, display: 'flex', justifyContent: 'center' }}>
