import React, { useEffect, useState, useRef } from 'react';
import {
    IonPage,
    IonHeader,
    IonToolbar,
    IonTitle,
    IonButtons,
    IonButton,
    IonIcon,
    IonContent,
    IonInput,
    IonSpinner,
    IonItem,
    IonLabel,
    IonAlert,
