styles/PaperTheme.css:
/* 
 * Á∫∏Ë¥®‰π¶ÂÜô‰∏ªÈ¢ò - ÂÖ®Â±Ä‰∏ªÈ¢òÂèòÈáè
 * Âü∫‰∫é Ionic È´òÁ∫ß‰∏ªÈ¢òÁ≥ªÁªü
 * Âº∫Âà∂‰ΩøÁî®‰∫ÆËâ≤Ê®°ÂºèÔºåÂøΩÁï•Á≥ªÁªüÊ∑±Ëâ≤Ê®°ÂºèËÆæÁΩÆ
 */

:root {
  /* Âº∫Âà∂Ë¶ÜÁõñÊ∑±Ëâ≤Ê®°ÂºèÔºåÁ°Æ‰øùÂßãÁªà‰ΩøÁî®‰∫ÆËâ≤‰∏ªÈ¢ò */
  color-scheme: light !important;
  /* ===== Á∫∏Ë¥®ÊÑüËâ≤ÂΩ©Á≥ªÁªü ===== */
  --paper-cream: #FDFCF9;
  --paper-white: #FFFFFF;
  --paper-black: #1a1a1a;
  --paper-aged: #F8F6F2;
  --ink-black: #2c2c2c;
  --ink-blue: #2563eb;
  --ink-brown: #8b4513;
  --muted-ink: #6b7280;
  --pencil-gray: #9ca3af;
  --line-color: #e5e7eb;
  --accent-brown: #7c5d3f;
  --accent-weak: #d1c7b8;
  --shadow-paper: rgba(0, 0, 0, 0.12);
  --shadow-deep: rgba(0, 0, 0, 0.18);

  /* ===== RGB Ê†ºÂºèÔºàÁî®‰∫éÈÄèÊòéÂ∫¶Ôºâ ===== */
  --paper-cream-rgb: 253, 252, 249;
  --ink-black-rgb: 44, 44, 44;
  --accent-brown-rgb: 124, 93, 63;
  --muted-ink-rgb: 107, 114, 128;

  /* ===== Ionic ‰∏ªÈ¢òËâ≤Ë¶ÜÁõñ ===== */
  --ion-color-primary: var(--accent-brown);
  --ion-color-primary-rgb: var(--accent-brown-rgb);
  --ion-color-primary-contrast: #ffffff;
  --ion-color-primary-contrast-rgb: 255, 255, 255;
  --ion-color-primary-shade: #654a36;
  --ion-color-primary-tint: #8a6b4f;

  /* Ê¨°Ë¶ÅÈ¢úËâ≤ - Ê∏©ÊöñÁöÑÁ∫∏Ë¥®ÊÑü */
  --ion-color-secondary: #d1c7b8;
  --ion-color-secondary-rgb: 209, 199, 184;
  --ion-color-secondary-contrast: #2c2c2c;
  --ion-color-secondary-contrast-rgb: 44, 44, 44;
  --ion-color-secondary-shade: #b8aea2;
  --ion-color-secondary-tint: #d6ccbf;

  /* ÊàêÂäüËâ≤ - Ëá™ÁÑ∂Áªø */
  --ion-color-success: #16a34a;
  --ion-color-success-rgb: 22, 163, 74;
  --ion-color-success-contrast: #ffffff;
  --ion-color-success-contrast-rgb: 255, 255, 255;
  --ion-color-success-shade: #15803d;
  --ion-color-success-tint: #22c55e;

  /* Ë≠¶ÂëäËâ≤ - Ê∏©ÊöñÊ©ô */
  --ion-color-warning: #ea580c;
  --ion-color-warning-rgb: 234, 88, 12;
  --ion-color-warning-contrast: #ffffff;
  --ion-color-warning-contrast-rgb: 255, 255, 255;
  --ion-color-warning-shade: #c2410c;
  --ion-color-warning-tint: #f97316;

  /* Âç±Èô©Ëâ≤ - Ê∏©ÂíåÁ∫¢ */
  --ion-color-danger: #dc2626;
  --ion-color-danger-rgb: 220, 38, 38;
  --ion-color-danger-contrast: #ffffff;
  --ion-color-danger-contrast-rgb: 255, 255, 255;
  --ion-color-danger-shade: #b91c1c;
  --ion-color-danger-tint: #ef4444;

  /* Ê∑±Ëâ≤ - Â¢®Ê∞¥Ëâ≤ */
  --ion-color-dark: var(--ink-black);
  --ion-color-dark-rgb: var(--ink-black-rgb);
  --ion-color-dark-contrast: var(--paper-cream);
  --ion-color-dark-contrast-rgb: var(--paper-cream-rgb);
  --ion-color-dark-shade: #111827;
  --ion-color-dark-tint: #374151;

  /* ‰∏≠Á≠âËâ≤ - ÈìÖÁ¨îÁÅ∞ */
  --ion-color-medium: var(--pencil-gray);
  --ion-color-medium-rgb: 156, 163, 175;
  --ion-color-medium-contrast: #ffffff;
  --ion-color-medium-contrast-rgb: 255, 255, 255;
  --ion-color-medium-shade: #6b7280;
  --ion-color-medium-tint: #d1d5db;

  /* ÊµÖËâ≤ - Á∫∏Âº†Ëâ≤ */
  --ion-color-light: var(--paper-cream);
  --ion-color-light-rgb: var(--paper-cream-rgb);
  --ion-color-light-contrast: var(--ink-black);
  --ion-color-light-contrast-rgb: var(--ink-black-rgb);
  --ion-color-light-shade: #e5e2da;
  --ion-color-light-tint: #faf9f6;

  /* ===== ÂÖ®Â±ÄÂ∫îÁî®ÂèòÈáè ===== */
  --ion-background-color: var(--paper-white);
  --ion-background-color-rgb: var(--paper-cream-rgb);

  --ion-text-color: var(--ink-black);
  --ion-text-color-rgb: var(--ink-black-rgb);

  --ion-border-color: var(--line-color);

  /* Â≠ó‰ΩìÁ≥ªÁªü - ‰π¶ÂÜôÊÑüÂ≠ó‰Ωì */
  --ion-font-family: 
    ui-serif, 
    Georgia, 
    Cambria, 
    "Times New Roman", 
    Times, 
    serif,
    -apple-system, 
    BlinkMacSystemFont, 
    "Segoe UI", 
    Roboto, 
    Helvetica, 
    Arial, 
    sans-serif;

  /* Èó¥Ë∑ùÁ≥ªÁªü */
  --ion-margin: 16px;
  --ion-padding: 16px;

  /* Âç†‰ΩçÁ¨¶ÈÄèÊòéÂ∫¶ */
  --ion-placeholder-opacity: 0.6;

  /* ===== Á∫∏Ë¥®ÊÑüÁâπÊïàÂèòÈáè ===== */
  --paper-texture-opacity: 0.02;
  --paper-grain-size: 16px;
  --paper-shadow-soft: 0 2px 8px rgba(0, 0, 0, 0.06);
  --paper-shadow-medium: 0 4px 16px rgba(0, 0, 0, 0.08);
  --paper-shadow-strong: 0 8px 32px rgba(0, 0, 0, 0.12);
  --paper-border-radius: 8px;
  --paper-border-radius-large: 12px;
}

/* ===== Base typography scaling ===== */
html {
  font-size: 16px;
}

@media (min-width: 1024px) {
  html {
    font-size: 24px; /* ~1.5x for desktop */
  }
}

/* ===== ÂÖ®Â±ÄÁ∫∏Ë¥®ÊÑüËÉåÊôØ ===== */
body {
  background: 
    /* Á∫∏Ë¥®Á∫πÁêÜÂè†Âä† */
    radial-gradient(circle at 20% 80%, rgba(122, 108, 93, 0.03) 0%, transparent 50%),
    radial-gradient(circle at 80% 20%, rgba(203, 187, 165, 0.04) 0%, transparent 50%),
    radial-gradient(circle at 40% 40%, rgba(74, 73, 71, 0.02) 0%, transparent 50%),
    /* ‰∏ªÊ∏êÂèòËÉåÊôØ */
    linear-gradient(135deg, #4A4947 0%, #3d3c3a 25%, #4A4947 50%, #3a3938 75%, #4A4947 100%),
    /* ÁªÜÂæÆÁöÑÁ∫∏Ë¥®Á∫πÁêÜ */
    repeating-linear-gradient(
      45deg,
      transparent,
      transparent 2px,
      rgba(255, 255, 255, 0.005) 2px,
      rgba(255, 255, 255, 0.005) 4px
    );
  font-family: var(--ion-font-family);
  color: var(--ion-text-color);
}

/* ‰∏∫ body Ê∑ªÂä†Á∫∏Ë¥®Á∫πÁêÜ‰º™ÂÖÉÁ¥† */
body::before {
  content: "";
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-image:
    /* ÈöèÊú∫ÁÇπÁä∂Á∫πÁêÜÊ®°ÊãüÁ∫∏Ë¥®ÊÑü */
    radial-gradient(circle at 25% 25%, rgba(255, 255, 255, 0.008) 1px, transparent 1px),
    radial-gradient(circle at 75% 75%, rgba(0, 0, 0, 0.01) 1px, transparent 1px),
    radial-gradient(circle at 50% 10%, rgba(203, 187, 165, 0.015) 1px, transparent 1px),
    radial-gradient(circle at 10% 90%, rgba(122, 108, 93, 0.012) 1px, transparent 1px);
  background-size: 
    40px 40px,
    60px 60px,
    80px 80px,
    100px 100px;
  background-position:
    0 0,
    20px 20px,
    40px 40px,
    60px 60px;
  pointer-events: none;
  z-index: -1;
}

/* ===== Á∫∏Ë¥®ÊÑüÁªÑ‰ª∂Âü∫Á°ÄÊ†∑Âºè ===== */

/* Âç°ÁâáÁ∫∏Ë¥®ÊÑü */
ion-card {
  --background: var(--paper-cream);
  --color: var(--ink-black);
  border: 1px solid var(--line-color);
  box-shadow: 
    var(--paper-shadow-medium),
    inset 0 1px 0 rgba(255, 255, 255, 0.8),
    inset 0 -1px 0 rgba(0, 0, 0, 0.05);
  border-radius: var(--paper-border-radius-large);
  position: relative;
}

/* ‰∏∫Âç°ÁâáÊ∑ªÂä†Á∫∏Ë¥®Á∫πÁêÜ */
ion-card::before {
  content: "";
  position: absolute;
  inset: 0;
  border-radius: inherit;
  pointer-events: none;
  background-image:
    radial-gradient(circle at 20% 30%, rgba(122, 108, 93, 0.015) 1px, transparent 1px),
    radial-gradient(circle at 80% 70%, rgba(203, 187, 165, 0.02) 1px, transparent 1px);
  background-size: 20px 20px, 24px 24px;
  background-position: 0 0, 8px 8px;
  z-index: 0;
}

/* Á°Æ‰øùÂç°ÁâáÂÜÖÂÆπÂú®Á∫πÁêÜ‰πã‰∏ä */
ion-card-header,
ion-card-content {
  position: relative;
  z-index: 1;
}

/* ÊåâÈíÆÁ∫∏Ë¥®ÊÑü */
ion-button {
  --border-radius: var(--paper-border-radius);
  --box-shadow: var(--paper-shadow-soft), inset 0 1px 0 rgba(255, 255, 255, 0.3);
  font-family: var(--ion-font-family);
}

ion-button:hover {
  --box-shadow: var(--paper-shadow-medium), inset 0 1px 0 rgba(255, 255, 255, 0.4);
}

/* ËæìÂÖ•Ê°ÜÁ∫∏Ë¥®ÊÑü */
ion-input,
ion-textarea {
  --background: var(--paper-white);
  --color: var(--ink-black);
  --border-color: var(--line-color);
  --border-radius: var(--paper-border-radius);
  --padding-start: 12px;
  --padding-end: 12px;
  --padding-top: 8px;
  --padding-bottom: 8px;
  font-family: var(--ion-font-family);
}

/* Â∑•ÂÖ∑Ê†èÁ∫∏Ë¥®ÊÑü */
ion-toolbar {
  --background: var(--paper-cream);
  --color: var(--ink-black);
  --border-color: var(--line-color);
  --padding-start: 16px;
  --padding-end: 16px;
}

/* Ê†áÈ¢òÁ∫∏Ë¥®ÊÑü */
ion-title {
  font-family: var(--ion-font-family);
  font-weight: 600;
  color: var(--ink-black);
}

/* ÂàóË°®Á∫∏Ë¥®ÊÑü */
ion-list {
  --background: transparent;
}

ion-item {
  --background: var(--paper-cream);
  --color: var(--ink-black);
  --border-color: var(--line-color);
  --padding-start: 16px;
  --padding-end: 16px;
  font-family: var(--ion-font-family);
}

/* Ê®°ÊÄÅÊ°ÜÁ∫∏Ë¥®ÊÑü */
ion-modal {
  --background: var(--paper-cream);
  --color: var(--ink-black);
}

/* ÊµÆÂä®ÊåâÈíÆÁ∫∏Ë¥®ÊÑü */
ion-fab-button {
  --background: linear-gradient(135deg, var(--accent-brown) 0%, #6b5d52 50%, var(--accent-brown) 100%);
  --color: #ffffff;
  --box-shadow: 
    0 6px 20px rgba(0, 0, 0, 0.15),
    0 2px 8px rgba(0, 0, 0, 0.1),
    inset 0 1px 0 rgba(255, 255, 255, 0.3),
    inset 0 -1px 0 rgba(0, 0, 0, 0.1);
  border: 1px solid rgba(255, 255, 255, 0.2);
}

/* ===== Âº∫Âà∂Ë¶ÜÁõñÊ∑±Ëâ≤Ê®°Âºè ===== */
/* Á°Æ‰øùÂú®‰ªª‰ΩïÊÉÖÂÜµ‰∏ãÈÉΩ‰ΩøÁî®‰∫ÆËâ≤Á∫∏Ë¥®‰∏ªÈ¢ò */
@media (prefers-color-scheme: dark) {
  :root {
    /* Âº∫Âà∂Ë¶ÜÁõñÊâÄÊúâÊ∑±Ëâ≤Ê®°ÂºèÂèòÈáè‰∏∫‰∫ÆËâ≤ÂÄº */
    color-scheme: light !important;

    /* ÈáçÊñ∞Â£∞ÊòéÊâÄÊúâ‰∏ªË¶ÅÈ¢úËâ≤ÂèòÈáèÔºåÁ°Æ‰øùÊ∑±Ëâ≤Ê®°Âºè‰∏ã‰πü‰ΩøÁî®‰∫ÆËâ≤ */
    --ion-background-color: var(--paper-cream) !important;
    --ion-background-color-rgb: var(--paper-cream-rgb) !important;
    --ion-text-color: var(--ink-black) !important;
    --ion-text-color-rgb: var(--ink-black-rgb) !important;
    --ion-border-color: var(--line-color) !important;

    /* Á°Æ‰øùÊâÄÊúâ Ionic ÁªÑ‰ª∂Âú®Ê∑±Ëâ≤Ê®°Âºè‰∏ã‰πü‰ΩøÁî®‰∫ÆËâ≤‰∏ªÈ¢ò */
    --ion-color-step-50: #fafafa !important;
    --ion-color-step-100: #f5f5f5 !important;
    --ion-color-step-150: #f0f0f0 !important;
    --ion-color-step-200: #ebebeb !important;
    --ion-color-step-250: #e6e6e6 !important;
    --ion-color-step-300: #e1e1e1 !important;
    --ion-color-step-350: #dcdcdc !important;
    --ion-color-step-400: #d7d7d7 !important;
    --ion-color-step-450: #d2d2d2 !important;
    --ion-color-step-500: #cdcdcd !important;
    --ion-color-step-550: #c8c8c8 !important;
    --ion-color-step-600: #c3c3c3 !important;
    --ion-color-step-650: #bebebe !important;
    --ion-color-step-700: #b9b9b9 !important;
    --ion-color-step-750: #b4b4b4 !important;
    --ion-color-step-800: #afafaf !important;
    --ion-color-step-850: #aaaaaa !important;
    --ion-color-step-900: #a5a5a5 !important;
    --ion-color-step-950: #a0a0a0 !important;
  }
}

/* Âº∫Âà∂ÊâÄÊúâÂÖÉÁ¥†‰ΩøÁî®‰∫ÆËâ≤Ê®°Âºè */
* {
  color-scheme: light !important;
}

/* Á°Æ‰øù body Âíå html ‰πüÂº∫Âà∂‰ΩøÁî®‰∫ÆËâ≤ */
html, body {
  color-scheme: light !important;
  background-color: var(--paper-cream) !important;
  color: var(--ink-black) !important;
  font-weight: 400;
}

/* Âº∫Âà∂ Ionic Â∫îÁî®ÂÆπÂô®‰ΩøÁî®‰∫ÆËâ≤ */
ion-app, 
.ion-page, 
ion-content {
  --background: var(--paper-cream) !important;
  --color: var(--ink-black) !important;
  background: var(--paper-cream) !important;
  color: var(--ink-black) !important;
}

/* Â¢ûÂº∫ÊñáÂ≠óÂØπÊØîÂ∫¶ */
h1, h2, h3, h4, h5, h6 {
  color: var(--ink-black) !important;
  font-weight: 600 !important;
}

p, span, div {
  color: var(--ink-black) !important;
}

/* Á°Æ‰øùÊâÄÊúâ Ionic ÁªÑ‰ª∂ÊñáÂ≠óÈ¢úËâ≤ */
ion-label, 
ion-title, 
ion-button, 
ion-item {
  --color: var(--ink-black) !important;
  color: var(--ink-black) !important;
}

/* Á°Æ‰øùÊ¨°Ë¶ÅÊñáÂ≠ó‰πüÊúâË∂≥Â§üÂØπÊØîÂ∫¶ */
.muted, 
.secondary {
  color: var(--muted-ink) !important;
}

/* ===== ÂìçÂ∫îÂºèÁ∫∏Ë¥®ÊÑüË∞ÉÊï¥ ===== */
@media (max-width: 768px) {
  :root {
    --ion-margin: 12px;
    --ion-padding: 12px;
    --paper-border-radius: 6px;
    --paper-border-radius-large: 10px;
  }
}

@media (max-width: 480px) {
  :root {
    --ion-margin: 8px;
    --ion-padding: 8px;
    --paper-border-radius: 4px;
    --paper-border-radius-large: 8px;
  }
}

styles/Welcome.module.css:
/* Welcome ÁªÑ‰ª∂Ê†∑Âºè - ÁÆÄÁ∫¶È£éÊ†º */

.welcomeContainer {
  background: #ffffff;
  min-height: 100vh;
  color: #333333;
  position: relative;
  padding: 0;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
}

/* Â§¥ÈÉ® */
.header {
  text-align: center;
  padding: 56px 16px 16px;
}

.logo {
  display: inline-block;
  margin-bottom: 12px;
}

.logoImage {
  width: 64px;
  height: 64px;
  border-radius: 12px;
  transition: opacity 0.2s ease;
  box-shadow: 0 0 10px 0 rgba(0, 0, 0, 0.1);
}

.logoImage:hover {
  opacity: 0.8;
}

.title {
  font-size: 2.1rem;
  font-weight: 600;
  margin: 0 0 6px 0;
  line-height: 1.2;
  letter-spacing: -0.02em;
}

.titleGradient {
  color: #1a1a1a;
}

.titleSubtext {
  color: #666666;
  font-weight: 400;
  margin-left: 6px;
}

.subtitle {
  font-size: 1rem;
  color: #666666;
  margin: 0;
  font-weight: 400;
}

/* Ê≠•È™§ */
.steps {
  display: flex;
  flex-direction: column;
  gap: 8px;
  padding: 8px 16px 16px;
  max-width: 560px;
  margin: 0 auto;
}

.step {
  padding: 12px 12px;
  background: #fff;
  border-radius: 12px;
  border: 1px solid rgba(0, 0, 0, 0.1);
  position: relative;
  text-align: center;
  transition: background-color 0.2s ease;
}

.stepContent {
  margin-top: 0;
}

.stepContent h3 {
  font-size: 1.1rem;
  font-weight: 500;
  color: #1a1a1a;
  margin: 0 0 4px 0;
}

.stepContent p {
  color: #666666;
  margin: 0;
  font-size: 0.95rem;
  line-height: 1.4;
}

.stepNumber {
  position: absolute;
  top: 8px;
  left: 8px;
  background: #e5e5e5;
  color: #666666;
  width: 20px;
  height: 20px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  font-weight: 500;
  font-size: 0.75rem;
}

/* Ë°åÂä®ÊåâÈíÆ */
.ctaSection {
  text-align: center;
  padding: 16px 16px;
  display: flex;
  flex-direction: column;
  gap: 8px;
  align-items: center;
}

.primaryBtn {
  background: #1a1a1a;
  color: #ffffff;
  border: none;
  border-radius: 6px;
  padding: 10px 18px;
  font-weight: 500;
  font-size: 0.95rem;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 8px;
  justify-content: center;
  transition: background-color 0.2s ease;
  width: 180px;
  max-width: 90%;
}

.primaryBtn:hover {
  background: #333333;
}

.primaryBtn:active {
  background: #000000;
}

.secondaryBtn {
  background: transparent;
  color: #666666;
  border: 1px solid #d5d5d5;
  border-radius: 6px;
  padding: 10px 18px;
  font-weight: 500;
  font-size: 0.95rem;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 8px;
  justify-content: center;
  transition: all 0.2s ease;
  width: 180px;
  max-width: 90%;
}

.secondaryBtn:hover {
  background: #fafafa;
  border-color: #999999;
}

/* Â∫ïÈÉ® */
.footer {
  text-align: center;
  padding: 8px 16px 16px;
  color: #999999;
  font-size: 0.85rem;
}

.footer p {
  margin: 0;
}

/* ÂìçÂ∫îÂºèËÆæËÆ° */
@media (max-width: 768px) {
  .header {
    padding: 40px 12px 16px;
  }

  .title {
    font-size: 2rem;
  }

  .subtitle {
    font-size: 1rem;
  }

  .steps {
    padding: 8px 12px 16px;
  }

  .step {
    padding: 12px 12px;
  }

  .ctaSection {
    padding: 16px 12px;
  }
}

@media (max-width: 480px) {
  .header {
    padding: 32px 12px 14px;
  }

  .logoImage {
    width: 52px;
    height: 52px;
  }

  .title {
    font-size: 1.7rem;
  }

  .primaryBtn, .secondaryBtn {
    width: 100%;
    max-width: 280px;
  }
}

/* Ê°åÈù¢Á´ØÊõ¥Á¥ßÂáëÁöÑÊ≠•È™§Â∏ÉÂ±Ä */
@media (min-width: 768px) {
  .steps {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    gap: 10px;
    max-width: 840px;
  }
  .step {
    text-align: left;
    padding: 12px;
  }
  .stepNumber {
    top: 10px;
    left: 10px;
  }
}

/* ÁÆÄÂåñÂä®Áîª */
@keyframes fadeIn {
  from {
    opacity: 0;
    transform: translateY(12px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

/* Âü∫Á°ÄËøõÂú∫Âä®Áîª */
.logo,
.title,
.subtitle,
.step,
.ctaSection {
  opacity: 0;
  animation: fadeIn 0.5s ease-out forwards;
}

.logo {
  animation-delay: 0s;
}

.title {
  animation-delay: 0.1s;
}

/* ===== Desktop typography scaling (~1.5x) ===== */
@media (min-width: 1024px) {
  .title { font-size: 2.9rem; }
  .subtitle { font-size: 1.25rem; }

  .stepContent h3 { font-size: 1.4rem; }
  .stepContent p { font-size: 1.175rem; }

  .primaryBtn, .secondaryBtn { font-size: 1.175rem; }
  .footer { font-size: 1.025rem; }
}

.subtitle {
  animation-delay: 0.2s;
}

.step:nth-child(1) { 
  animation-delay: 0.3s;
}

.step:nth-child(2) { 
  animation-delay: 0.4s;
}

.step:nth-child(3) { 
  animation-delay: 0.5s;
}

.ctaSection {
  animation-delay: 0.6s;
}
