/**
 * 优惠券创建组件
 * 
 * 提供优惠券创建表单，包括金额输入、验证和优惠券生成功能
 * 支持显示生成的优惠券信息和图片保存功能
 */

import React, { useState } from 'react';
import { 
  IonButton, 
  IonInput, 
  IonItem, 
  IonLabel, 
  IonSpinner,
  IonCard,
  IonCardContent,
  IonCardHeader,
  IonCardTitle,
  IonText,
  IonIcon
} from '@ionic/react';
import { alertCircleOutline, checkmarkCircleOutline } from 'ionicons/icons';
import { validateAmount } from '../utils/validators';
import useCouponManager from '../hooks/useCouponManager';
import useImageGenerator from '../hooks/useImageGenerator';
import useErrorHandler from '../hooks/useErrorHandler';
import LoadingSpinner from './LoadingSpinner';
import styles from '../styles/CouponCreator.module.css';

const CouponCreator = ({ onCouponCreated }) => {
  const [amount, setAmount] = useState('');
  const [amountError, setAmountError] = useState('');

  const { 
    createCoupon, 
    isLoading: isCouponLoading, 
    error: couponError, 
    clearError
  } = useCouponManager();

  const { 
    handleValidationError,
    handleStorageError,
    withLoading
  } = useErrorHandler();

  /**
   * 处理金额输入变化
   */
  const handleAmountChange = (e) => {
    const value = e.detail.value;
    setAmount(value);

    // 清除之前的错误
    if (amountError) {
      setAmountError('');
    }
    if (couponError) {
      clearError();
    }
  };

  /**
   * 验证金额输入
   */
  const validateAmountInput = () => {
    if (!amount.trim()) {
      const errorMessage = '请输入优惠券金额';
      setAmountError(errorMessage);
      handleValidationError(errorMessage);
      return false;
    }

    const numAmount = parseFloat(amount);
    if (!validateAmount(numAmount)) {
      const errorMessage = '请输入有效的金额（大于0的数字）';
      setAmountError(errorMessage);
      handleValidationError(errorMessage);
      return false;
    }

    return true;
  };

  /**
   * 处理创建优惠券
   */
  const handleCreateCoupon = async () => {
    if (!validateAmountInput()) {
      return;
    }

    const result = await withLoading(async () => {
      const numAmount = parseFloat(amount);
      const coupon = await createCoupon(numAmount);

      if (!coupon) {
        throw new Error(couponError || '创建优惠券失败');
      }

      return coupon;
    }, '创建优惠券失败，请重试');

    if (result.success) {
      // 创建成功，清空输入并通知父组件
      setAmount('');
      setAmountError('');
      if (onCouponCreated) {
        onCouponCreated(result.data);
      }
    } else {
      handleStorageError(result.error);
    }
  };

  return (
    <div className={styles.container}>
      <div className={styles.createForm}>
        <IonCard className={styles.formCard}>
          <IonCardHeader>
            <IonCardTitle>创建优惠券</IonCardTitle>
          </IonCardHeader>
          <IonCardContent>
            <IonItem className={styles.inputItem}>
              <IonLabel position="stacked">优惠券金额 (元)</IonLabel>
              <IonInput
                type="number"
                placeholder="请输入金额"
                value={amount}
                onIonInput={handleAmountChange}
                className={amountError ? styles.inputError : ''}
              />
            </IonItem>

            {amountError && (
              <IonText color="danger" className={styles.errorText}>
                <p>
                  <IonIcon icon={alertCircleOutline} className={styles.errorIcon} />
                  {amountError}
                </p>
              </IonText>
            )}

            {couponError && (
              <IonText color="danger" className={styles.errorText}>
                <p>
                  <IonIcon icon={alertCircleOutline} className={styles.errorIcon} />
                  {couponError}
                </p>
              </IonText>
            )}

            <IonButton
              expand="block"
              onClick={handleCreateCoupon}
              disabled={isCouponLoading}
              className={`${styles.createButton} ${isCouponLoading ? styles.buttonLoading : ''}`}
            >
              {isCouponLoading ? (
                <>
                  <IonSpinner name="crescent" />
                  <span className={styles.buttonText}>创建中...</span>
                </>
              ) : (
                '创建优惠券'
              )}
            </IonButton>
          </IonCardContent>
        </IonCard>
      </div>
    </div>
  );
};

export default CouponCreator;

components/CouponDetailModal.jsx:
/**
 * 优惠券详情 Modal 组件
 * 显示优惠券完整信息，支持删除和保存图片操作
 */

import { 
  IonModal,
  IonHeader,
  IonToolbar,
  IonTitle,
  IonButtons,
  IonButton,
  IonContent,
  IonIcon,
  IonText,
  IonBadge,
  IonAlert
} from '@ionic/react';
import { 
  close,
  checkmarkCircleOutline,
  closeCircleOutline,
  timeOutline,
  imageOutline,
  trashOutline
} from 'ionicons/icons';
import { useState } from 'react';
import styles from '../styles/CouponDetailModal.module.css';

const CouponDetailModal = ({ 
  isOpen, 
  coupon, 
  onClose, 
  onDelete,
  onSaveImage,
  isDeleting,
  isSavingImage
}) => {
  const [showDeleteConfirm, setShowDeleteConfirm] = useState(false);

  /**
   * 格式化日期
   */
  const formatDate = (date) => {
    if (!date) return '';
    try {
      const d = new Date(date);
      // 检查日期是否有效
      if (isNaN(d.getTime())) {
        console.error('Invalid date:', date);
        return '日期无效';
      }
      return `${d.getFullYear()}-${String(d.getMonth() + 1).padStart(2, '0')}-${String(d.getDate()).padStart(2, '0')} ${String(d.getHours()).padStart(2, '0')}:${String(d.getMinutes()).padStart(2, '0')}:${String(d.getSeconds()).padStart(2, '0')}`;
    } catch (error) {
      console.error('Error formatting date:', error);
      return '日期错误';
    }
  };

  const handleDelete = () => {
    setShowDeleteConfirm(true);
  };

  const confirmDelete = () => {
    setShowDeleteConfirm(false);
    onDelete(coupon.code);
  };

  const cancelDelete = () => {
    setShowDeleteConfirm(false);
  };

  if (!coupon) return null;

  return (
    <>
      <IonModal isOpen={isOpen} onDidDismiss={onClose}>
        <IonHeader>
          <IonToolbar>
            <IonTitle>优惠券详情</IonTitle>
            <IonButtons slot="end">
              <IonButton onClick={onClose}>
                <IonIcon icon={close} />
              </IonButton>
            </IonButtons>
          </IonToolbar>
        </IonHeader>

        <IonContent className={styles.content}>
          <div className={styles.detailContainer}>
            {/* 金额展示 */}
            <div className={styles.amountCard}>
              <IonText className={styles.amountLabel}>优惠金额</IonText>
              <div className={styles.amountDisplay}>
                <IonText className={styles.currency}>¥</IonText>
                <IonText className={styles.amount}>{coupon.amount}</IonText>
              </div>
            </div>

            {/* 优惠券信息 */}
            <div className={styles.infoSection}>
              <div className={styles.infoItem}>
                <IonText className={styles.infoLabel}>优惠券编码</IonText>
                <IonText className={styles.infoValue}>
                  {coupon.code}
                </IonText>
              </div>

              <div className={styles.infoItem}>
                <IonText className={styles.infoLabel}>使用状态</IonText>
                <IonBadge 
                  color={coupon.isUsed ? 'medium' : 'success'}
                  className={styles.statusBadge}
                >
                  <IonIcon 
                    icon={coupon.isUsed ? closeCircleOutline : checkmarkCircleOutline}
                    className={styles.statusIcon}
                  />
                  {coupon.isUsed ? '已使用' : '未使用'}
                </IonBadge>
              </div>

              <div className={styles.infoItem}>
                <IonText className={styles.infoLabel}>
                  <IonIcon icon={timeOutline} className={styles.labelIcon} />
                  创建时间
                </IonText>
                <IonText className={styles.infoValue}>
                  {formatDate(coupon.createdAt)}
                </IonText>
              </div>

              {coupon.isUsed && coupon.usedAt && (
                <div className={styles.infoItem}>
                  <IonText className={styles.infoLabel}>
                    <IonIcon icon={timeOutline} className={styles.labelIcon} />
                    使用时间
                  </IonText>
                  <IonText className={styles.infoValue}>
                    {formatDate(coupon.usedAt)}
                  </IonText>
                </div>
              )}
            </div>

            {/* 操作按钮 */}
            <div className={styles.actionButtons}>
              <IonButton
                expand="block"
                fill="solid"
                onClick={() => onSaveImage(coupon)}
                disabled={isSavingImage}
                className={styles.saveButton}
              >
                <IonIcon icon={imageOutline} slot="start" />
                {isSavingImage ? '保存中...' : '保存图片'}
              </IonButton>

              <IonButton
                expand="block"
                fill="outline"
                color="danger"
                onClick={handleDelete}
                disabled={isDeleting}
                className={styles.deleteButton}
              >
                <IonIcon icon={trashOutline} slot="start" />
                {isDeleting ? '删除中...' : '删除优惠券'}
              </IonButton>
            </div>
          </div>
        </IonContent>
      </IonModal>

      {/* 删除确认对话框 */}
      <IonAlert
        isOpen={showDeleteConfirm}
        onDidDismiss={cancelDelete}
        header="确认删除"
        message={`确定要删除这张 ¥${coupon.amount} 的优惠券吗？删除后将无法恢复。`}
        buttons={[
          {
            text: '取消',
            role: 'cancel',
            handler: cancelDelete
          },
          {
            text: '确认删除',
            role: 'destructive',
            handler: confirmDelete
          }
        ]}
      />
    </>
  );
};

export default CouponDetailModal;

components/CouponList.jsx:
/**
 * 优惠券列表组件
 * 显示所有优惠券，支持点击查看详情
 */

import { 
  IonCard, 
  IonCardContent,
  IonText,
  IonIcon,
  IonBadge
} from '@ionic/react';
import { 
  checkmarkCircleOutline, 
  closeCircleOutline,
  timeOutline
} from 'ionicons/icons';
import styles from '../styles/CouponList.module.css';

const CouponList = ({ coupons, onCouponClick }) => {
  /**
   * 加密显示优惠券码（显示前2位和后2位，中间用*代替）
   */
  const maskCouponCode = (code) => {
    if (!code || code.length < 4) return code;
    return `${code.substring(0, 2)}**${code.substring(code.length - 2)}`;
  };

  /**
   * 格式化日期
   */
  const formatDate = (date) => {
    if (!date) return '';
    try {
      const d = new Date(date);
      // 检查日期是否有效
      if (isNaN(d.getTime())) {
        console.error('Invalid date:', date);
        return '日期无效';
      }
      return `${d.getFullYear()}-${String(d.getMonth() + 1).padStart(2, '0')}-${String(d.getDate()).padStart(2, '0')} ${String(d.getHours()).padStart(2, '0')}:${String(d.getMinutes()).padStart(2, '0')}`;
    } catch (error) {
      console.error('Error formatting date:', error);
      return '日期错误';
    }
  };

  if (!coupons || coupons.length === 0) {
    return (
      <div className={styles.emptyState}>
        <IonText color="medium">
          <p>暂无优惠券</p>
        </IonText>
      </div>
    );
  }

  return (
    <div className={styles.listContainer}>
      {coupons.map((coupon) => (
        <IonCard 
          key={coupon.code} 
          className={styles.couponItem}
          onClick={() => onCouponClick(coupon)}
          button
        >
          <IonCardContent className={styles.cardContent}>
            <div className={styles.leftSection}>
              <div className={styles.amountSection}>
                <IonText className={styles.currency}>¥</IonText>
                <IonText className={styles.amount}>{coupon.amount}</IonText>
              </div>
            </div>

            <div className={styles.rightSection}>
              <div className={styles.codeSection}>
                <IonText className={styles.codeLabel}>编码</IonText>
                <IonText className={styles.codeValue}>
                  {maskCouponCode(coupon.code)}
                </IonText>
              </div>

              <div className={styles.statusSection}>
                <IonBadge 
                  color={coupon.isUsed ? 'medium' : 'success'}
                  className={styles.statusBadge}
                >
                  <IonIcon 
                    icon={coupon.isUsed ? closeCircleOutline : checkmarkCircleOutline}
                    className={styles.statusIcon}
                  />
                  {coupon.isUsed ? '已使用' : '未使用'}
                </IonBadge>
              </div>

              <div className={styles.dateSection}>
                <IonIcon icon={timeOutline} className={styles.dateIcon} />
                <IonText className={styles.dateText}>
                  {formatDate(coupon.createdAt)}
                </IonText>
              </div>
            </div>
          </IonCardContent>
        </IonCard>
      ))}
    </div>
  );
};

export default CouponList;

components/CouponResultModal.jsx:
/**
 * 优惠券创建结果 Modal 组件
 * 显示新创建的优惠券信息
 */

import { 
  IonModal,
  IonHeader,
  IonToolbar,
  IonTitle,
  IonButtons,
  IonButton,
  IonContent,
  IonIcon,
  IonText
} from '@ionic/react';
import { 
  close,
  checkmarkCircleOutline,
  imageOutline
} from 'ionicons/icons';
import styles from '../styles/CouponResultModal.module.css';

const CouponResultModal = ({ 
  isOpen, 
  coupon, 
  onClose,
  onSaveImage,
  isSavingImage
}) => {
  if (!coupon) return null;

  return (
    <IonModal isOpen={isOpen} onDidDismiss={onClose}>
      <IonHeader>
        <IonToolbar>
          <IonTitle>创建成功</IonTitle>
          <IonButtons slot="end">
            <IonButton onClick={onClose}>
              <IonIcon icon={close} />
            </IonButton>
          </IonButtons>
        </IonToolbar>
      </IonHeader>

      <IonContent className={styles.content}>
        <div className={styles.resultContainer}>
          {/* 成功提示 */}
          <div className={styles.successHeader}>
            <IonIcon icon={checkmarkCircleOutline} className={styles.successIcon} />
            <IonText className={styles.successText}>
              <h2>优惠券创建成功！</h2>
            </IonText>
          </div>

          {/* 优惠券信息卡片 */}
          <div className={styles.couponCard}>
            <div className={styles.amountSection}>
              <IonText className={styles.amountLabel}>优惠金额</IonText>
              <div className={styles.amountDisplay}>
                <IonText className={styles.currency}>¥</IonText>
                <IonText className={styles.amount}>{coupon.amount}</IonText>
              </div>
            </div>

            <div className={styles.codeSection}>
