          )}
        </IonContent>
      </IonModal>

      {/* 验券确认对话框 */}
      <IonAlert
        isOpen={showUseConfirm}
        onDidDismiss={cancelUse}
        header="确认验券"
        message={`确定要使用这张 ¥${result?.coupon?.amount} 的优惠券吗？使用后将无法撤销。`}
        buttons={[
          {
            text: '取消',
            role: 'cancel',
            handler: cancelUse
          },
          {
            text: '确认使用',
            handler: confirmUse
          }
        ]}
      />
    </>
  );
};

