/**
 * 优惠券管理自定义Hook
 * 封装优惠券创建、查询、使用逻辑和状态管理
 */

import { useState, useCallback } from 'react';
import CouponService from '../services/CouponService';

const useCouponManager = () => {
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [coupons, setCoupons] = useState([]);
  const [currentCoupon, setCurrentCoupon] = useState(null);

  // 创建优惠券服务实例
  const couponService = new CouponService();

  /**
   * 创建新优惠券
   * @param {number} amount - 优惠券金额
   * @returns {Promise<Object|null>} 创建的优惠券对象
   */
  const createCoupon = useCallback(async (amount) => {
    setIsLoading(true);
    setError(null);

    try {
      const coupon = await couponService.createCoupon(amount);
      setCurrentCoupon(coupon);

      // 更新优惠券列表
      setCoupons(prev => [coupon, ...prev]);

      return coupon;
    } catch (err) {
      setError(err.message);
      return null;
    } finally {
      setIsLoading(false);
    }
  }, []);

  /**
   * 验证优惠券
   * @param {string} code - 优惠券编码
   * @returns {Promise<Object|null>} 验证结果
   */
  const validateCoupon = useCallback(async (code) => {
    setIsLoading(true);
    setError(null);

    try {
      const result = await couponService.validateCoupon(code);
      return result;
    } catch (err) {
      setError(err.message);
      return null;
    } finally {
      setIsLoading(false);
    }
  }, []);

  /**
   * 使用优惠券
   * @param {string} code - 优惠券编码
   * @returns {Promise<boolean>} 是否成功使用
   */
  const useCoupon = useCallback(async (code) => {
    setIsLoading(true);
    setError(null);

    try {
      const success = await couponService.useCoupon(code);

      if (success) {
        // 更新本地状态中的优惠券
        setCoupons(prev => prev.map(coupon => 
          coupon.code === code 
            ? { ...coupon, isUsed: true, usedAt: new Date() }
            : coupon
        ));
      }

      return success;
    } catch (err) {
      setError(err.message);
      return false;
    } finally {
      setIsLoading(false);
    }
  }, []);

  /**
   * 获取单个优惠券
   * @param {string} code - 优惠券编码
   * @returns {Promise<Object|null>} 优惠券对象
   */
  const getCoupon = useCallback(async (code) => {
    setIsLoading(true);
    setError(null);

    try {
      const coupon = await couponService.getCoupon(code);
      return coupon;
    } catch (err) {
      setError(err.message);
      return null;
    } finally {
      setIsLoading(false);
    }
  }, []);

  /**
   * 获取所有优惠券
   * @returns {Promise<Array>} 优惠券列表
   */
  const getAllCoupons = useCallback(async () => {
    setIsLoading(true);
    setError(null);

    try {
      const allCoupons = await couponService.getAllCoupons();
      setCoupons(allCoupons);
      return allCoupons;
    } catch (err) {
      setError(err.message);
      return [];
    } finally {
      setIsLoading(false);
    }
  }, []);

  /**
   * 清除错误状态
   */
  const clearError = useCallback(() => {
    setError(null);
  }, []);

  /**
   * 清除当前优惠券
   */
  const clearCurrentCoupon = useCallback(() => {
    setCurrentCoupon(null);
  }, []);

  return {
    // 状态
    isLoading,
    error,
    coupons,
    currentCoupon,

    // 操作方法
    createCoupon,
    validateCoupon,
    useCoupon,
    getCoupon,
    getAllCoupons,
    clearError,
    clearCurrentCoupon
  };
};

export default useCouponManager;

hooks/useErrorHandler.js:
/**
 * 全局错误处理 Hook
 * 
 * 提供统一的错误处理、Toast 提示和加载状态管理
 */

import { useState, useCallback } from 'react';

/**
 * 错误类型枚举
 */
export const ErrorType = {
  VALIDATION: 'validation',
  NETWORK: 'network', 
  STORAGE: 'storage',
  IMAGE: 'image',
  UNKNOWN: 'unknown'
};

/**
 * 错误严重程度
 */
export const ErrorSeverity = {
  INFO: 'info',
  WARNING: 'warning', 
  ERROR: 'error',
  CRITICAL: 'critical'
};

/**
 * 全局错误处理 Hook
 */
const useErrorHandler = () => {
  const [errors, setErrors] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [toasts, setToasts] = useState([]);

  /**
   * 添加错误
   */
  const addError = useCallback((error, type = ErrorType.UNKNOWN, severity = ErrorSeverity.ERROR) => {
    const errorId = Date.now() + Math.random();
    const errorObj = {
      id: errorId,
      message: typeof error === 'string' ? error : error.message || '未知错误',
      type,
      severity,
      timestamp: new Date(),
      details: typeof error === 'object' ? error : null
    };

    setErrors(prev => [...prev, errorObj]);

    // 自动清除非关键错误
    if (severity !== ErrorSeverity.CRITICAL) {
      setTimeout(() => {
        clearError(errorId);
      }, 5000);
    }

    return errorId;
  }, []);

  /**
   * 清除特定错误
   */
  const clearError = useCallback((errorId) => {
    setErrors(prev => prev.filter(error => error.id !== errorId));
  }, []);

  /**
   * 清除所有错误
   */
  const clearAllErrors = useCallback(() => {
    setErrors([]);
  }, []);

  /**
   * 显示 Toast 提示
   */
  const showToast = useCallback((message, type = 'info', duration = 3000) => {
    const toastId = Date.now() + Math.random();
    const toast = {
      id: toastId,
      message,
      type,
      duration,
      timestamp: new Date()
    };

    setToasts(prev => [...prev, toast]);

    // 自动移除 Toast
    setTimeout(() => {
      setToasts(prev => prev.filter(t => t.id !== toastId));
    }, duration);

    return toastId;
  }, []);

  /**
   * 移除 Toast
   */
  const removeToast = useCallback((toastId) => {
    setToasts(prev => prev.filter(t => t.id !== toastId));
  }, []);

  /**
   * 异步操作包装器
   */
  const withLoading = useCallback(async (asyncFn, errorMessage = '操作失败') => {
    try {
      setIsLoading(true);
      const result = await asyncFn();
      return { success: true, data: result };
    } catch (error) {
      console.error('异步操作错误:', error);
      addError(error.message || errorMessage, ErrorType.UNKNOWN, ErrorSeverity.ERROR);
      return { success: false, error: error.message || errorMessage };
    } finally {
      setIsLoading(false);
    }
  }, [addError]);

  /**
   * 处理验证错误
   */
  const handleValidationError = useCallback((message) => {
    return addError(message, ErrorType.VALIDATION, ErrorSeverity.WARNING);
  }, [addError]);

  /**
   * 处理网络错误
   */
  const handleNetworkError = useCallback((error) => {
    const message = '网络连接失败，请检查网络设置';
    return addError(message, ErrorType.NETWORK, ErrorSeverity.ERROR);
  }, [addError]);

  /**
   * 处理存储错误
   */
  const handleStorageError = useCallback((error) => {
    const message = '数据保存失败，请重试';
    return addError(message, ErrorType.STORAGE, ErrorSeverity.ERROR);
  }, [addError]);

  /**
   * 处理图片生成错误
   */
  const handleImageError = useCallback((error) => {
    const message = '图片生成失败，请重试';
    return addError(message, ErrorType.IMAGE, ErrorSeverity.ERROR);
  }, [addError]);

  /**
   * 获取最新错误
   */
  const getLatestError = useCallback(() => {
    return errors.length > 0 ? errors[errors.length - 1] : null;
  }, [errors]);

  /**
   * 获取特定类型的错误
   */
  const getErrorsByType = useCallback((type) => {
    return errors.filter(error => error.type === type);
  }, [errors]);

  return {
    // 状态
    errors,
    isLoading,
    toasts,

    // 错误管理
    addError,
    clearError,
    clearAllErrors,
    getLatestError,
    getErrorsByType,

    // Toast 管理
    showToast,
    removeToast,

    // 工具方法
    withLoading,
    handleValidationError,
    handleNetworkError,
    handleStorageError,
    handleImageError
  };
};

export default useErrorHandler;

hooks/useImageGenerator.js:
/**
 * 图片生成自定义Hook
 * 封装Canvas图片生成逻辑和状态管理
 */

import { useState, useCallback } from 'react';
import ImageService from '../services/ImageService';

const useImageGenerator = () => {
  const [isGenerating, setIsGenerating] = useState(false);
  const [isSaving, setIsSaving] = useState(false);
  const [error, setError] = useState(null);
  const [generatedImage, setGeneratedImage] = useState(null);

  // 创建图片服务实例
  const imageService = new ImageService();

  /**
   * 生成优惠券图片
   * @param {Object} coupon - 优惠券对象
   * @param {Object} config - 图片配置（可选）
   * @returns {Promise<string|null>} Base64格式的图片数据
   */
  const generateImage = useCallback(async (coupon, config = null) => {
    setIsGenerating(true);
    setError(null);

    try {
      const base64Data = await imageService.generateCouponImage(coupon, config);
      setGeneratedImage(base64Data);
      return base64Data;
    } catch (err) {
      setError(err.message);
      return null;
    } finally {
      setIsGenerating(false);
    }
  }, []);

  /**
   * 保存图片到相册
   * @param {string} base64Data - Base64格式的图片数据
   * @param {string} filename - 文件名（可选）
   * @returns {Promise<Object>} 保存结果
   */
  const saveToAlbum = useCallback(async (base64Data, filename = null) => {
    setIsSaving(true);
    setError(null);

    try {
      const result = await imageService.saveImageToAlbum(base64Data, filename);

      if (!result.success && result.error) {
        setError(result.error);
      }

      return result;
    } catch (err) {
      setError(err.message);
      return {
        success: false,
        filename: filename || null,
        error: err.message
      };
    } finally {
      setIsSaving(false);
    }
  }, []);

  /**
   * 生成并保存优惠券图片
   * @param {Object} coupon - 优惠券对象
   * @param {Object} config - 图片配置（可选）
   * @returns {Promise<Object>} 生成和保存结果
   */
  const generateAndSave = useCallback(async (coupon, config = null) => {
    setIsGenerating(true);
    setIsSaving(true);
    setError(null);

    try {
      const result = await imageService.generateAndSaveImage(coupon, config);

      if (result.success && result.base64Data) {
        setGeneratedImage(result.base64Data);
      }

      return result;
    } catch (err) {
      setError(err.message);
      return {
        success: false,
        base64Data: null,
        filename: null,
        error: err.message
      };
    } finally {
      setIsGenerating(false);
      setIsSaving(false);
    }
  }, []);

  /**
   * 清除生成的图片
   */
  const clearImage = useCallback(() => {
    setGeneratedImage(null);
  }, []);

  /**
   * 清除错误状态
   */
  const clearError = useCallback(() => {
    setError(null);
  }, []);

  return {
    // 状态
    isGenerating,
    isSaving,
    error,
    generatedImage,

    // 操作方法
    generateImage,
    saveToAlbum,
    generateAndSave,
    clearImage,
    clearError
  };
};

export default useImageGenerator;

services/CouponService.js:
/**
 * 优惠券业务服务
 * 负责优惠券的创建、验证和状态管理
 */

import { COUPON_CODE_LENGTH, COUPON_CODE_CHARS } from '../utils/constants';
import { createCoupon, createValidationResult, CouponErrorType } from '../utils/types';
import { validateAmount, validateCouponCode } from '../utils/validators';
import StorageService from './StorageService';

class CouponService {
  constructor() {
    this.storageService = new StorageService();
  }

  /**
   * 生成6位唯一优惠券编码
   * @returns {string} 6位编码
   */
  generateCouponCode() {
    let code = '';
    for (let i = 0; i < COUPON_CODE_LENGTH; i++) {
      const randomIndex = Math.floor(Math.random() * COUPON_CODE_CHARS.length);
      code += COUPON_CODE_CHARS[randomIndex];
    }
    return code;
  }

  /**
   * 创建新优惠券
   * @param {number} amount - 优惠券金额
   * @returns {Promise<Object>} 创建的优惠券对象
   * @throws {Error} 当金额无效时抛出错误
   */
  async createCoupon(amount) {
    if (!validateAmount(amount)) {
      throw new Error('Invalid amount format');
    }

    // 生成唯一编码，确保不重复
    let code;
    let attempts = 0;
    const maxAttempts = 10;

    do {
      code = this.generateCouponCode();
      attempts++;

      if (attempts > maxAttempts) {
        throw new Error('Failed to generate unique coupon code');
      }
    } while (await this.storageService.getCoupon(code));

    const coupon = createCoupon({
      id: code, // 使用编码作为ID
      code,
      amount: Number(amount),
      isUsed: false,
      createdAt: new Date()
    });

    return await this.storageService.saveCoupon(coupon);
  }

  /**
   * 验证优惠券编码
   * @param {string} code - 优惠券编码
   * @returns {Promise<Object>} 验证结果
   */
  async validateCoupon(code) {
    if (!validateCouponCode(code)) {
      return createValidationResult({
        isValid: false,
        message: '优惠券编码格式无效',
        canUse: false
      });
    }

    try {
      const coupon = await this.storageService.getCoupon(code);

      if (!coupon) {
        return createValidationResult({
          isValid: false,
          message: '优惠券不存在',
          canUse: false
        });
      }

      if (coupon.isUsed) {
        return createValidationResult({
          isValid: true,
          coupon,
          message: '优惠券已使用',
          canUse: false
        });
      }

      return createValidationResult({
        isValid: true,
        coupon,
        message: '优惠券有效',
        canUse: true
      });
    } catch (error) {
      return createValidationResult({
        isValid: false,
        message: '查询优惠券时发生错误',
        canUse: false
      });
    }
  }

  /**
   * 使用优惠券
   * @param {string} code - 优惠券编码
   * @returns {Promise<boolean>} 是否成功使用
   */
  async useCoupon(code) {
    try {
      const coupon = await this.storageService.getCoupon(code);

      if (!coupon) {
        throw new Error('Coupon not found');
      }

      if (coupon.isUsed) {
        throw new Error('Coupon already used');
      }

      const updatedCoupon = await this.storageService.updateCoupon(code, {
        ...coupon??{},
        isUsed: true,
        usedAt: new Date()
      });

      return !!updatedCoupon;
    } catch (error) {
      console.error('Error using coupon:', error);
      return false;
    }
  }

  /**
   * 获取所有优惠券
   * @returns {Promise<Array>} 优惠券列表
   */
  async getAllCoupons() {
    try {
      return await this.storageService.getAllCoupons();
    } catch (error) {
      console.error('Error getting all coupons:', error);
      return [];
    }
  }

  /**
   * 获取单个优惠券
   * @param {string} code - 优惠券编码
   * @returns {Promise<Object|null>} 优惠券对象或null
   */
  async getCoupon(code) {
    try {
      return await this.storageService.getCoupon(code);
    } catch (error) {
      console.error('Error getting coupon:', error);
      return null;
    }
  }
}

export default CouponService;

services/ImageService.js:
/**
 * 图片生成服务
 * 基于Canvas实现优惠券图片生成和AppSdk.fileSystem.saveImageToAlbum保存功能
 */

import AppSdk from '@morphixai/app-sdk';
import { createCouponImageConfig, CouponErrorType, createCouponError } from '../utils/types';

class ImageService {
  constructor() {
    // 默认图片配置
    this.defaultConfig = createCouponImageConfig();
  }

  /**
   * 生成优惠券图片
   * @param {Object} coupon - 优惠券对象
   * @param {Object} config - 可选的图片配置
   * @returns {Promise<string>} Base64编码的图片数据
   */
  async generateCouponImage(coupon, config = {}) {
    try {
      const imageConfig = { ...this.defaultConfig, ...config };

      // 创建Canvas元素
      const canvas = document.createElement('canvas');
      const ctx = canvas.getContext('2d');

      // 设置画布尺寸
      canvas.width = imageConfig.width;
      canvas.height = imageConfig.height;

      // 绘制背景渐变
      this._drawBackground(ctx, imageConfig);

      // 绘制优惠券内容
      this._drawCouponContent(ctx, coupon, imageConfig);

      // 转换为Base64
      const base64Data = canvas.toDataURL('image/png');

      return base64Data;
    } catch (error) {
      console.error('Error generating coupon image:', error);
      throw createCouponError({
        type: CouponErrorType.IMAGE_GENERATION_ERROR,
        message: 'Failed to generate coupon image',
        details: error
      });
    }
  }

  /**
   * 保存图片到设备相册
   * @param {string} base64Data - Base64编码的图片数据
   * @param {string} filename - 可选的文件名
   * @returns {Promise<Object>} 保存结果
   */
  async saveImageToAlbum(base64Data, filename) {
    try {
      // 提取纯Base64数据（去除data:image/png;base64,前缀）
      const pureBase64 = base64Data.replace(/^data:image\/[a-z]+;base64,/, '');

      // 生成文件名（如果未提供）
      const finalFilename = filename || `coupon_${Date.now()}.png`;

      // 调用AppSdk保存到相册
      const result = await AppSdk.fileSystem.saveImageToAlbum({
        base64Data: pureBase64,
        filename: finalFilename
      });

      return {
        success: result.success,
        filename: finalFilename,
        error: result.error || null
      };
    } catch (error) {
      console.error('Error saving image to album:', error);
      return {
        success: false,
        filename: filename || null,
        error: error.message || 'Failed to save image'
      };
    }
  }

  /**
   * 生成并保存优惠券图片到相册
   * @param {Object} coupon - 优惠券对象
   * @param {Object} config - 可选的图片配置
   * @returns {Promise<Object>} 生成和保存结果
   */
  async generateAndSaveImage(coupon, config = {}) {
    try {
      // 生成图片
      const base64Data = await this.generateCouponImage(coupon, config);

      // 生成文件名
      const filename = `coupon_${coupon.code}_${Date.now()}.png`;

      // 保存到相册
      const saveResult = await this.saveImageToAlbum(base64Data, filename);

      return {
        success: saveResult.success,
        base64Data: saveResult.success ? base64Data : null,
        filename: saveResult.filename,
        error: saveResult.error
      };
    } catch (error) {
      console.error('Error generating and saving image:', error);
      return {
        success: false,
        base64Data: null,
        filename: null,
        error: error.message || 'Failed to generate and save image'
      };
    }
  }

  /**
   * 绘制背景渐变
   * @param {CanvasRenderingContext2D} ctx - Canvas上下文
   * @param {Object} config - 图片配置
   * @private
   */
  _drawBackground(ctx, config) {
    // 创建渐变背景
    const gradient = ctx.createLinearGradient(0, 0, config.width, config.height);
    gradient.addColorStop(0, '#4F46E5'); // 蓝紫色
    gradient.addColorStop(1, '#7C3AED'); // 紫色

    // 填充背景
    ctx.fillStyle = gradient;
    ctx.fillRect(0, 0, config.width, config.height);

    // 添加圆角效果（通过绘制圆角矩形）
    ctx.save();
    ctx.globalCompositeOperation = 'destination-in';
    this._drawRoundedRect(ctx, 0, 0, config.width, config.height, 12);
    ctx.restore();
  }

  /**
   * 绘制优惠券内容
   * @param {CanvasRenderingContext2D} ctx - Canvas上下文
   * @param {Object} coupon - 优惠券对象
   * @param {Object} config - 图片配置
   * @private
   */
  _drawCouponContent(ctx, coupon, config) {
    ctx.fillStyle = config.textColor;
    ctx.textAlign = 'center';
    ctx.textBaseline = 'middle';

    const centerX = config.width / 2;
    const centerY = config.height / 2;

    // 绘制"优惠券"标题
    ctx.font = `${config.fontSize.label}px -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', sans-serif`;
    ctx.fillText('优惠券', centerX, centerY - 50);

    // 绘制金额
    ctx.font = `bold ${config.fontSize.amount}px -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', sans-serif`;
    ctx.fillText(`¥${coupon.amount}`, centerX, centerY - 10);

    // 绘制编码标签
    ctx.font = `${config.fontSize.label}px -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', sans-serif`;
    ctx.fillText('优惠券编码', centerX, centerY + 25);

    // 绘制编码
    ctx.font = `${config.fontSize.code}px 'Courier New', monospace`;
    ctx.fillText(coupon.code, centerX, centerY + 50);

    // 如果已使用，添加水印
    if (coupon.isUsed) {
      this._drawUsedWatermark(ctx, config);
    }
  }

  /**
   * 绘制"已使用"水印
   * @param {CanvasRenderingContext2D} ctx - Canvas上下文
   * @param {Object} config - 图片配置
   * @private
   */
  _drawUsedWatermark(ctx, config) {
    ctx.save();

    // 设置半透明红色
    ctx.fillStyle = 'rgba(239, 68, 68, 0.8)';
    ctx.strokeStyle = 'rgba(239, 68, 68, 1)';
    ctx.lineWidth = 2;

    // 旋转画布
    ctx.translate(config.width / 2, config.height / 2);
    ctx.rotate(-Math.PI / 6); // -30度

    // 绘制水印文字
    ctx.font = `bold 28px -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', sans-serif`;
    ctx.textAlign = 'center';
    ctx.textBaseline = 'middle';

    // 绘制文字描边和填充
    ctx.strokeText('已使用', 0, 0);
    ctx.fillText('已使用', 0, 0);

    ctx.restore();
  }

  /**
   * 绘制圆角矩形
   * @param {CanvasRenderingContext2D} ctx - Canvas上下文
   * @param {number} x - X坐标
   * @param {number} y - Y坐标
   * @param {number} width - 宽度
   * @param {number} height - 高度
   * @param {number} radius - 圆角半径
   * @private
   */
  _drawRoundedRect(ctx, x, y, width, height, radius) {
    ctx.beginPath();
    ctx.moveTo(x + radius, y);
    ctx.lineTo(x + width - radius, y);
    ctx.quadraticCurveTo(x + width, y, x + width, y + radius);
    ctx.lineTo(x + width, y + height - radius);
    ctx.quadraticCurveTo(x + width, y + height, x + width - radius, y + height);
    ctx.lineTo(x + radius, y + height);
    ctx.quadraticCurveTo(x, y + height, x, y + height - radius);
    ctx.lineTo(x, y + radius);
    ctx.quadraticCurveTo(x, y, x + radius, y);
    ctx.closePath();
    ctx.fill();
  }

  /**
   * 获取默认图片配置
   * @returns {Object} 默认配置对象
   */
  getDefaultConfig() {
    return { ...this.defaultConfig };
  }

  /**
   * 更新默认图片配置
   * @param {Object} newConfig - 新的配置
   */
  updateDefaultConfig(newConfig) {
    this.defaultConfig = { ...this.defaultConfig, ...newConfig };
  }
}

export default ImageService;

services/StorageService.js:
/**
 * 云端存储服务
 * 基于AppSdk.appData实现优惠券数据的云端存储
 */

import AppSdk from '@morphixai/app-sdk';
import { STORAGE_COLLECTION_NAME } from '../utils/constants';

class StorageService {
  constructor() {
    this.collectionName = STORAGE_COLLECTION_NAME;
  }

  /**
   * 保存优惠券到云端存储
   * @param {Object} coupon - 优惠券对象
   * @returns {Promise<Object>} 保存的优惠券对象
   */
  async saveCoupon(coupon) {
    try {
      // 使用优惠券编码作为记录ID
      const result = await AppSdk.appData.createData({
        collection: this.collectionName,
        data: {
          id: coupon.code, // 使用优惠券编码作为固定ID
          ...coupon,
          // 确保日期对象被正确序列化
          createdAt: coupon.createdAt.toISOString(),
          usedAt: coupon.usedAt ? coupon.usedAt.toISOString() : null
        }
      });

      return this._deserializeCoupon(result);
    } catch (error) {
      console.error('Error saving coupon:', error);
      throw new Error('Failed to save coupon');
    }
  }

  /**
   * 从云端存储获取优惠券
   * @param {string} code - 优惠券编码
   * @returns {Promise<Object|null>} 优惠券对象或null
   */
  async getCoupon(code) {
    try {
      const result = await AppSdk.appData.getData({
        collection: this.collectionName,
        id: code
      });
      return result ? this._deserializeCoupon(result) : null;
    } catch (error) {
      console.error('Error getting coupon:', error);
      return null;
    }
  }

  /**
   * 获取所有优惠券
   * @returns {Promise<Array>} 优惠券列表
   */
  async getAllCoupons() {
    try {
      // 查询所有优惠券（不设置查询条件返回所有数据）
      const result = await AppSdk.appData.queryData({
        collection: this.collectionName,
        query: []
      });
      return result.map(record => this._deserializeCoupon(record));
    } catch (error) {
      console.error('Error getting all coupons:', error);
      return [];
    }
  }

  /**
   * 更新优惠券
   * @param {string} code - 优惠券编码
   * @param {Object} updates - 更新数据
   * @returns {Promise<Object|null>} 更新后的优惠券对象
   */
  async updateCoupon(code, updates) {
    try {
      // 先获取现有数据
      const existingCoupon = await this.getCoupon(code);
      if (!existingCoupon) {
        return null;
      }

      // 合并更新数据
      const mergedData = {
        ...existingCoupon,
        ...updates
      };

      // 准备更新数据（序列化日期）
      const updateData = {};
      for (const [key, value] of Object.entries(updates)) {
        if (value instanceof Date) {
          updateData[key] = value.toISOString();
        } else {
          updateData[key] = value;
        }
      }

      // 执行更新
      await AppSdk.appData.updateData({
        collection: this.collectionName,
        id: code,
        data: updateData
      });

      // 更新后重新获取完整数据，确保数据完整性
      const updatedCoupon = await this.getCoupon(code);
      return updatedCoupon;
    } catch (error) {
      console.error('Error updating coupon:', error);
      return null;
    }
  }

  /**
   * 删除优惠券
   * @param {string} code - 优惠券编码
   * @returns {Promise<boolean>} 是否成功删除
   */
  async deleteCoupon(code) {
    try {
      const result = await AppSdk.appData.deleteData({
        collection: this.collectionName,
        id: code
      });
      return result.success;
    } catch (error) {
      console.error('Error deleting coupon:', error);
      return false;
    }
  }

  /**
   * 反序列化优惠券数据（将字符串日期转换为Date对象）
   * @param {Object} data - 序列化的优惠券数据
   * @returns {Object} 反序列化的优惠券对象
   * @private
   */
  _deserializeCoupon(data) {
    return {
      ...data,
      createdAt: new Date(data.createdAt),
      usedAt: data.usedAt ? new Date(data.usedAt) : null
    };
  }
}

export default StorageService;

