/* Welcome 组件样式 - 简约风格 */

.welcomeContainer {
  background: #ffffff;
  min-height: 100vh;
  color: #333333;
  position: relative;
  padding: 0;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
}

/* 头部 */
.header {
  text-align: center;
  padding: 56px 16px 16px;
}

.logo {
  display: inline-block;
  margin-bottom: 12px;
}

.logoImage {
  width: 64px;
  height: 64px;
  border-radius: 12px;
  transition: opacity 0.2s ease;
  box-shadow: 0 0 10px 0 rgba(0, 0, 0, 0.1);
}

.logoImage:hover {
  opacity: 0.8;
}

.title {
  font-size: 2.1rem;
  font-weight: 600;
  margin: 0 0 6px 0;
  line-height: 1.2;
  letter-spacing: -0.02em;
}

.titleGradient {
  color: #1a1a1a;
}

.titleSubtext {
  color: #666666;
  font-weight: 400;
  margin-left: 6px;
}

.subtitle {
  font-size: 1rem;
  color: #666666;
  margin: 0;
  font-weight: 400;
}

/* 步骤 */
.steps {
  display: flex;
  flex-direction: column;
  gap: 8px;
  padding: 8px 16px 16px;
  max-width: 560px;
  margin: 0 auto;
}

.step {
  padding: 12px 12px;
  background: #fff;
  border-radius: 12px;
  border: 1px solid rgba(0, 0, 0, 0.1);
  position: relative;
  text-align: center;
  transition: background-color 0.2s ease;
}

.stepContent {
  margin-top: 0;
}

.stepContent h3 {
  font-size: 1.1rem;
  font-weight: 500;
  color: #1a1a1a;
  margin: 0 0 4px 0;
}

.stepContent p {
  color: #666666;
  margin: 0;
  font-size: 0.95rem;
  line-height: 1.4;
}

.stepNumber {
  position: absolute;
  top: 8px;
  left: 8px;
  background: #e5e5e5;
  color: #666666;
  width: 20px;
  height: 20px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  font-weight: 500;
  font-size: 0.75rem;
}

/* 行动按钮 */
.ctaSection {
  text-align: center;
  padding: 16px 16px;
  display: flex;
  flex-direction: column;
  gap: 8px;
  align-items: center;
}

.primaryBtn {
  background: #1a1a1a;
  color: #ffffff;
  border: none;
  border-radius: 6px;
  padding: 10px 18px;
  font-weight: 500;
  font-size: 0.95rem;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 8px;
  justify-content: center;
  transition: background-color 0.2s ease;
  width: 180px;
  max-width: 90%;
}

.primaryBtn:hover {
  background: #333333;
}

.primaryBtn:active {
  background: #000000;
}

.secondaryBtn {
  background: transparent;
  color: #666666;
  border: 1px solid #d5d5d5;
  border-radius: 6px;
  padding: 10px 18px;
  font-weight: 500;
  font-size: 0.95rem;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 8px;
  justify-content: center;
  transition: all 0.2s ease;
  width: 180px;
  max-width: 90%;
}

.secondaryBtn:hover {
  background: #fafafa;
  border-color: #999999;
}

/* 底部 */
.footer {
  text-align: center;
  padding: 8px 16px 16px;
  color: #999999;
  font-size: 0.85rem;
}

.footer p {
  margin: 0;
}

/* 响应式设计 */
@media (max-width: 768px) {
  .header {
    padding: 40px 12px 16px;
  }

  .title {
    font-size: 2rem;
  }

  .subtitle {
    font-size: 1rem;
  }

  .steps {
    padding: 8px 12px 16px;
  }

  .step {
    padding: 12px 12px;
  }

  .ctaSection {
    padding: 16px 12px;
  }
}

@media (max-width: 480px) {
  .header {
    padding: 32px 12px 14px;
  }

  .logoImage {
    width: 52px;
    height: 52px;
  }

  .title {
    font-size: 1.7rem;
  }

  .primaryBtn, .secondaryBtn {
    width: 100%;
    max-width: 280px;
  }
}

/* 桌面端更紧凑的步骤布局 */
@media (min-width: 768px) {
  .steps {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    gap: 10px;
    max-width: 840px;
  }
  .step {
    text-align: left;
    padding: 12px;
  }
  .stepNumber {
    top: 10px;
    left: 10px;
  }
}

/* 简化动画 */
@keyframes fadeIn {
  from {
    opacity: 0;
    transform: translateY(12px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

/* 基础进场动画 */
.logo,
.title,
.subtitle,
.step,
.ctaSection {
  opacity: 0;
  animation: fadeIn 0.5s ease-out forwards;
}

.logo {
  animation-delay: 0s;
}

.title {
  animation-delay: 0.1s;
}

.subtitle {
  animation-delay: 0.2s;
}

.step:nth-child(1) { 
  animation-delay: 0.3s;
}

.step:nth-child(2) { 
  animation-delay: 0.4s;
}

.step:nth-child(3) { 
  animation-delay: 0.5s;
}

.ctaSection {
  animation-delay: 0.6s;
}

