.container {
  height: 100%;
  display: flex;
  flex-direction: column;
}

.content {
  padding: 20px;
  max-width: 600px;
  margin: 0 auto;
  width: 100%;
}

.typeSelector {
  display: flex;
  gap: 12px;
  margin-bottom: 32px;
}

.typeButton {
  flex: 1;
  height: 48px;
  border: 2px solid var(--border-color);
  background: var(--bg-primary);
  color: var(--text-primary);
  border-radius: 12px;
  font-size: 16px;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.2s;
}

.typeButton:active {
  transform: scale(0.98);
}

.typeButtonActive {
  border-color: var(--primary-color);
  background: var(--primary-color);
  color: white;
}

.amountSection {
  margin-bottom: 32px;
}

.amountLabel {
  font-size: 14px;
  color: var(--text-secondary);
  margin-bottom: 12px;
}

.amountInput {
  display: flex;
  align-items: center;
  gap: 8px;
}

.currency {
  font-size: 32px;
  font-weight: 700;
  color: var(--text-primary);
}

.input {
  flex: 1;
  font-size: 32px;
  font-weight: 700;
  border: none;
  outline: none;
  background: transparent;
  color: var(--text-primary);
  padding: 8px 0;
}

.input::placeholder {
  color: var(--text-secondary);
  opacity: 0.5;
}

.section {
  margin-bottom: 32px;
}

.sectionLabel {
  font-size: 14px;
  color: var(--text-secondary);
  margin-bottom: 12px;
}

.categories {
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  gap: 12px;
}

.categoryButton {
  height: 48px;
  border: 2px solid var(--border-color);
  background: var(--bg-primary);
  color: var(--text-primary);
  border-radius: 12px;
  font-size: 14px;
  cursor: pointer;
  transition: all 0.2s;
}

.categoryButton:active {
  transform: scale(0.95);
}

.categoryButtonActive {
  border-color: var(--primary-color);
  background: var(--primary-color);
  color: white;
}

.textarea {
  width: 100%;
  padding: 12px;
  border: 2px solid var(--border-color);
  border-radius: 12px;
  font-size: 14px;
  font-family: inherit;
  resize: none;
  background: var(--bg-primary);
  color: var(--text-primary);
  outline: none;
}

.textarea:focus {
  border-color: var(--primary-color);
