import { initializeSettings, getSettings, updateSettings } from '../services/dataService';

/**
 * ä»ªå¼çŠ¶æ€ç®¡ç†
 */
export const useRitualStore = create((set, get) => ({
  // è®¾ç½®
  settings: null,

  // å½“å‰ä»ªå¼çŠ¶æ€
  currentRitual: {
    step: 'welcome', // welcome, setup, emotion, coach, focus, complete
    emotion: null,
    energy: 3,
    note: '',
    tasks: [],
    aiAdvice: null,
  },

  // ä¸“æ³¨è®¡æ—¶å™¨çŠ¶æ€
  focusTimer: {
    isRunning: false,
    remainingTime: 1500, // 25åˆ†é’Ÿ
    startTime: null,
  },

  // åŠ è½½çŠ¶æ€
  isLoading: false,
  error: null,

  // åˆå§‹åŒ–
  initialize: async () => {
    set({ isLoading: true });
    try {
      const settings = await initializeSettings();
      set({ 
        settings,
        focusTimer: {
          ...get().focusTimer,
          remainingTime: settings?.focusDuration || 1500,
        },
        isLoading: false 
      });
    } catch (error) {
      set({ error: error.message, isLoading: false });
    }
  },

  // æ›´æ–°è®¾ç½®
  updateSettings: async (newSettings) => {
    try {
      await updateSettings(newSettings);
      const settings = await getSettings();
      set({ settings });
    } catch (error) {
      set({ error: error.message });
    }
  },

  // è®¾ç½®ä»ªå¼æ­¥éª¤
  setRitualStep: (step) => {
    set(state => ({
      currentRitual: {
        ...state.currentRitual,
        step,
      },
    }));
  },

  // æ›´æ–°æƒ…ç»ªçŠ¶æ€
  updateEmotion: (emotion, energy, note) => {
    set(state => ({
      currentRitual: {
        ...state.currentRitual,
        emotion,
        energy,
        note: note || '',
      },
    }));
  },

  // è®¾ç½®ä»»åŠ¡
  setTasks: (tasks) => {
    set(state => ({
      currentRitual: {
        ...state.currentRitual,
        tasks,
      },
    }));
  },

  // è®¾ç½® AI å»ºè®®
  setAIAdvice: (advice) => {
    set(state => ({
      currentRitual: {
        ...state.currentRitual,
        aiAdvice: advice,
      },
    }));
  },

  // å¼€å§‹ä¸“æ³¨è®¡æ—¶
  startFocusTimer: () => {
    const { settings } = get();
    set({
      focusTimer: {
        isRunning: true,
        remainingTime: settings?.focusDuration || 1500,
        startTime: Date.now(),
      },
    });
  },

  // æš‚åœä¸“æ³¨è®¡æ—¶
  pauseFocusTimer: () => {
    set(state => ({
      focusTimer: {
        ...state.focusTimer,
        isRunning: false,
      },
    }));
  },

  // æ¢å¤ä¸“æ³¨è®¡æ—¶
  resumeFocusTimer: () => {
    set(state => ({
      focusTimer: {
        ...state.focusTimer,
        isRunning: true,
      },
    }));
  },

  // æ›´æ–°å‰©ä½™æ—¶é—´
  updateRemainingTime: (time) => {
    set(state => ({
      focusTimer: {
        ...state.focusTimer,
        remainingTime: time,
      },
    }));
  },

  // é‡ç½®ä¸“æ³¨è®¡æ—¶
  resetFocusTimer: () => {
    const { settings } = get();
    set({
      focusTimer: {
        isRunning: false,
        remainingTime: settings?.focusDuration || 1500,
        startTime: null,
      },
    });
  },

  // å®Œæˆä»ªå¼
  completeRitual: () => {
    set({
      currentRitual: {
        step: 'welcome',
        emotion: null,
        energy: 3,
        note: '',
        tasks: [],
        aiAdvice: null,
      },
    });
    get().resetFocusTimer();
  },

  // é‡ç½®ä»ªå¼
  resetRitual: () => {
    set({
      currentRitual: {
        step: 'welcome',
        emotion: null,
        energy: 3,
        note: '',
        tasks: [],
        aiAdvice: null,
      },
    });
    get().resetFocusTimer();
  },
}));

