// æ•°æ®å­˜å‚¨æœåŠ¡
import AppSdk from '@morphixai/app-sdk';
import { reportError } from '@morphixai/lib';

export class DataService {
  static COLLECTION = 'soulTestRecords';

  // ä¿å­˜æµ‹è¯•è®°å½•
  static async saveTestRecord(testData) {
    try {
      const record = {
        ...testData,
        createdAt: Date.now(),
        updatedAt: Date.now()
      };

      const result = await AppSdk.appData.createData({
        collection: this.COLLECTION,
        data: record
      });

      console.log('æµ‹è¯•è®°å½•ä¿å­˜æˆåŠŸ:', result);
      return result;
    } catch (error) {
      await reportError(error, 'JavaScriptError', {
        component: 'DataService',
        action: 'saveTestRecord'
      });

      // é™çº§åˆ° localStorage
      try {
        const history = JSON.parse(localStorage.getItem('testHistory') || '[]');
        const record = {
          id: Date.now().toString(),
          ...testData,
          createdAt: Date.now()
        };
        history.unshift(record);
        if (history.length > 50) {
          history.splice(50);
        }
        localStorage.setItem('testHistory', JSON.stringify(history));
        return record;
      } catch (localError) {
        console.error('æœ¬åœ°å­˜å‚¨ä¹Ÿå¤±è´¥:', localError);
        return null;
      }
    }
  }

  // è·å–æ‰€æœ‰æµ‹è¯•è®°å½•
  static async getAllRecords() {
    try {
      const result = await AppSdk.appData.queryData({
        collection: this.COLLECTION,
        query: []
      });

      // æŒ‰æ—¶é—´å€’åºæ’åº
      const sorted = (result || []).sort((a, b) => 
        (b.createdAt || 0) - (a.createdAt || 0)
      );

      console.log('è·å–æµ‹è¯•è®°å½•æˆåŠŸï¼Œæ•°é‡:', sorted.length);
      return sorted;
    } catch (error) {
      await reportError(error, 'JavaScriptError', {
        component: 'DataService',
        action: 'getAllRecords'
      });

      // é™çº§åˆ° localStorage
      try {
        const history = JSON.parse(localStorage.getItem('testHistory') || '[]');
        return history;
      } catch (localError) {
        console.error('æœ¬åœ°å­˜å‚¨è¯»å–å¤±è´¥:', localError);
        return [];
      }
    }
  }

  // åˆ é™¤æµ‹è¯•è®°å½•
  static async deleteRecord(id) {
    try {
      const result = await AppSdk.appData.deleteData({
        collection: this.COLLECTION,
        id: id
      });

      console.log('åˆ é™¤è®°å½•æˆåŠŸ:', id);
      return result.success;
    } catch (error) {
      await reportError(error, 'JavaScriptError', {
        component: 'DataService',
        action: 'deleteRecord',
        recordId: id
      });

      // é™çº§åˆ° localStorage
      try {
        const history = JSON.parse(localStorage.getItem('testHistory') || '[]');
        const filtered = history.filter(record => record.id !== id);
        localStorage.setItem('testHistory', JSON.stringify(filtered));
        return true;
      } catch (localError) {
        console.error('æœ¬åœ°å­˜å‚¨åˆ é™¤å¤±è´¥:', localError);
        return false;
      }
    }
  }

  // ä¿å­˜ç”¨æˆ·åå¥½è®¾ç½®
  static savePreference(key, value) {
    try {
      localStorage.setItem(`pref_${key}`, JSON.stringify(value));
      return true;
    } catch (error) {
      console.error('ä¿å­˜åå¥½è®¾ç½®å¤±è´¥:', error);
      return false;
    }
  }

  // è·å–ç”¨æˆ·åå¥½è®¾ç½®
  static getPreference(key, defaultValue = null) {
    try {
      const value = localStorage.getItem(`pref_${key}`);
      return value ? JSON.parse(value) : defaultValue;
    } catch (error) {
      console.error('è·å–åå¥½è®¾ç½®å¤±è´¥:', error);
      return defaultValue;
    }
  }
}

