  overflow: hidden;
}

.progressBar {
  height: 100%;
  background: linear-gradient(90deg, #a18fff, #d8c3ff);
  transition: width 0.3s ease;
}

/* 问题卡片 */
.questionCard {
  position: relative;
  z-index: 2;
  width: 100%;
  max-width: 600px;
  padding: 40px 30px;
  background: rgba(255, 255, 255, 0.03);
  border-radius: 24px;
  margin: 40px 20px;
  box-shadow: 0 0 20px rgba(153, 132, 255, 0.1);
  backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 255, 255, 0.08);
  transition: transform 0.3s ease;
}

.questionIntro {
  font-size: 16px;
  color: #ffffff !important;
  margin-bottom: 20px;
  text-align: center;
  letter-spacing: 0.5px;
  opacity: 0;
  animation: fadeIn 0.5s ease-out forwards;
}

.questionTitle {
  font-size: 18px;
  color: #aaaaaa;
  margin-bottom: 15px;
  text-align: center;
  display: flex;
  justify-content: space-between;
