}

.questionText {
  font-size: 20px;
  line-height: 1.8;
  color: #ffffff;
  margin-bottom: 30px;
  text-align: center;
  font-weight: 500;
  letter-spacing: 0.5px;
  word-break: break-word;
  opacity: 0;
  animation: fadeIn 0.5s ease-out 0.1s forwards;
}

/* 选项组 */
.optionsGroup {
  width: 100%;
  display: flex;
  flex-direction: column;
  gap: 20px;
  margin-top: 30px;
}

.optionItem {
  display: flex;
  align-items: flex-start;
  padding: 20px;
  background: rgba(255, 255, 255, 0.06);
  border-radius: 16px;
  border: 1px solid rgba(255, 255, 255, 0.05);
  transition: all 0.3s ease;
  cursor: pointer;
  position: relative;
  overflow: hidden;
}

.optionItem:hover {
  transform: translateY(-2px);
  background: rgba(161, 143, 255, 0.08);
}

.optionItem:active {
  transform: scale(0.98);
}

.optionSelected {
  background: rgba(161, 143, 255, 0.15);
  border-color: rgba(161, 143, 255, 0.4);
  transform: translateY(-2px);
  box-shadow: 0 4px 12px rgba(161, 143, 255, 0.2);
}

.optionRadio {
  width: 22px;
  height: 22px;
  min-width: 22px;
  border-radius: 50%;
  border: 2px solid rgba(255, 255, 255, 0.3);
  background: transparent;
  margin-right: 15px;
  position: relative;
  transition: all 0.3s ease;
}

.optionSelected .optionRadio {
  background: #a18fff;
  border-color: #a18fff;
}

.optionSelected .optionRadio::after {
  content: '';
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 10px;
  height: 10px;
  background: #ffffff;
  border-radius: 50%;
}

.optionText {
  flex: 1;
  color: #e6e6e6;
  font-size: 16px;
  line-height: 1.6;
}

/* 最后一题提示 */
.finalTip {
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 20px;
  margin: 20px 0;
  background: rgba(161, 143, 255, 0.1);
  border-radius: 16px;
  border: 1px solid rgba(161, 143, 255, 0.2);
  animation: tipGlow 2s ease-in-out infinite;
}

.tipIcon {
  font-size: 32px;
  margin-bottom: 10px;
  animation: float 3s ease-in-out infinite;
}

.tipText {
  font-size: 18px;
  color: #ffffff;
  margin-bottom: 5px;
  text-align: center;
}

.tipSubtext {
  font-size: 14px;
  color: #ffffff !important;
  text-align: center;
}

/* 按钮 */
.btnNext {
