            {/* åªåœ¨ä¼‘æ¯æ—¶é—´æ˜¾ç¤ºè·³è¿‡æŒ‰é’® */}
            {isBreak && (
              <IonButton
                fill="clear"
                size="large"
                onClick={handleSkipBreak}
                className={styles.skipButton}
              >
                <IonIcon 
                  icon={leaf} 
