import { create } from 'zustand';
import AppSdk from '@morphixai/app-sdk';
import { reportError } from '@morphixai/lib';
import { 
  debugReminder, 
  cleanupTimerRemindersSafely, 
  createReminderSafely, 
  deleteReminderSafely,
  handleReminderError 
} from '../utils/errorHandlers';

export const useTimerStore = create((set, get) => ({
  isRunning: false,
  mode: 'focus',
  remainingTime: 25 * 60,
  duration: 25 * 60,
  shortBreak: 5 * 60,
  longBreak: 15 * 60,
  currentSession: 1,
  totalSessions: 4,
  plants: [],
  completedPomodoros: 0,
  totalFocusTime: 0,
  currentReminderId: null,
  onReminderError: null, // æé†’é”™è¯¯å›è°ƒå‡½æ•°

  initializeTimer: async () => {
    try {
      console.log('Initializing timer...');
      const timerData = await AppSdk.appData.getData({
        collection: 'timer',
        id: 'current'
      });

      if (timerData) {
        console.log('Found existing timer data:', timerData);
        set({
          isRunning: timerData.isRunning || false,
          mode: timerData.mode || 'focus',
          remainingTime: timerData.remainingTime || 25 * 60,
          duration: timerData.duration || 25 * 60,
          shortBreak: timerData.shortBreak || 5 * 60,
          longBreak: timerData.longBreak || 15 * 60,
          currentSession: timerData.currentCycle || 1,
          totalSessions: timerData.cycles || 4,
          completedPomodoros: timerData.completedPomodoros || 0,
          totalFocusTime: timerData.totalFocusTime || 0,
          plants: timerData.plants || []
        });
      }

      const statsData = await AppSdk.appData.getData({
        collection: 'stats',
        id: 'history'
      });

      if (statsData) {
        console.log('Found existing stats data:', statsData);
        set({
          completedPomodoros: statsData.completedPomodoros || get().completedPomodoros,
          totalFocusTime: statsData.totalFocusTime || get().totalFocusTime
        });
      }

      console.log('Timer initialization completed');
    } catch (error) {
      console.error('Failed to initialize timer:', error);
      await reportError(error, 'TimerInitError');
    }
  },

  tick: async () => {
    const { remainingTime, isRunning } = get();

    if (!isRunning || remainingTime <= 0) {
      return;
    }

    const newRemainingTime = remainingTime - 1;

    if (newRemainingTime <= 0) {
      await get().completeSession();
    } else {
      set({ remainingTime: newRemainingTime });

      if (newRemainingTime % 10 === 0) {
        await get().saveTimerState();
      }
    }
  },

  restoreTimer: async () => {
    try {
      const { currentReminderId, isRunning } = get();

      if (!isRunning || !currentReminderId) {
        return;
      }

      console.log('Restoring timer from background...');

      const reminder = await AppSdk.reminder.getReminder({ id: currentReminderId });

      if (!reminder) {
        console.log('Reminder not found, timer may have completed');
        await get().completeSession();
        return;
      }

      const now = Date.now();
      const reminderTime = reminder.start_time;
      const timeLeft = Math.max(0, Math.floor((reminderTime - now) / 1000));

      console.log(`Timer restored: ${timeLeft} seconds remaining`);

      if (timeLeft <= 0) {
        await get().completeSession();
      } else {
        set({ remainingTime: timeLeft });
        await get().saveTimerState();
      }

    } catch (error) {
      console.error('Failed to restore timer:', error);
      await reportError(error, 'TimerRestoreError');
    }
  },

  saveTimerState: async () => {
    try {
      const state = get();

      await AppSdk.appData.updateData({
        collection: 'timer',
        id: 'current',
        data: {
          isRunning: state.isRunning,
          mode: state.mode,
          remainingTime: state.remainingTime,
          duration: state.duration,
          shortBreak: state.shortBreak,
          longBreak: state.longBreak,
          currentCycle: state.currentSession,
          cycles: state.totalSessions,
          completedPomodoros: state.completedPomodoros,
          totalFocusTime: state.totalFocusTime,
          plants: state.plants,
          lastUpdated: Date.now()
        }
      });

    } catch (error) {
      console.error('Failed to save timer state:', error);
    }
  },

  startTimer: async () => {
    try {
      const { remainingTime, mode } = get();

      // ç«‹å³å¯åŠ¨è®¡æ—¶å™¨ï¼Œä¸ç­‰å¾…æé†’åˆ›å»º
      set({ 
        isRunning: true
      });

      await get().saveTimerState();

      // å¼‚æ­¥åˆ›å»ºæé†’ï¼Œä¸é˜»å¡è®¡æ—¶å™¨è¿è¡Œ
      createReminderSafely({
        message: mode === 'focus' ? 'ì§‘ì¤‘ ì‹œê°„ì´ ëë‚¬ìŠµë‹ˆë‹¤!' : 'íœ´ì‹ ì‹œê°„ì´ ëë‚¬ìŠµë‹ˆë‹¤!',
        start_time: Date.now() + (remainingTime * 1000),
        title: 'ìì—° ì •ì› ë½€ëª¨ë„ë¡œ',
        sub_title: mode === 'focus' ? 'íœ´ì‹ì„ ì·¨í•˜ì„¸ìš”' : 'ë‹¤ì‹œ ì§‘ì¤‘í•  ì‹œê°„ì…ë‹ˆë‹¤'
      }).then(reminderResult => {
        if (reminderResult.success) {
          console.log('æé†’åˆ›å»ºæˆåŠŸ:', reminderResult.reminder.id);
          // æ›´æ–° reminderId
          set({ currentReminderId: reminderResult.reminder.id });
          get().saveTimerState();
        } else {
          console.warn('æé†’åˆ›å»ºå¤±è´¥ï¼Œä½†è®¡æ—¶å™¨ç»§ç»­è¿è¡Œ:', reminderResult.error);
          // é€šçŸ¥ UI å±‚æ˜¾ç¤ºé”™è¯¯æç¤º
          if (get().onReminderError) {
            get().onReminderError(reminderResult.error);
          }
        }
      }).catch(error => {
        console.error('æé†’åˆ›å»ºå¼‚å¸¸:', error);
        if (get().onReminderError) {
          get().onReminderError(error.message || 'æé†’åˆ›å»ºå¤±è´¥');
        }
      });

    } catch (error) {
      console.error('Failed to start timer:', error);
      await reportError(error, 'TimerStartError');
      // å¦‚æœå¯åŠ¨è®¡æ—¶å™¨æœ¬èº«å¤±è´¥ï¼Œæ¢å¤çŠ¶æ€
      set({ isRunning: false });
      throw error; // å‘ä¸ŠæŠ›å‡ºï¼Œè®© UI å±‚å¤„ç†
    }
  },

  pauseTimer: async () => {
    try {
      const { currentReminderId } = get();

      if (currentReminderId) {
        const deleteResult = await deleteReminderSafely(currentReminderId);
        if (!deleteResult.success) {
          console.warn('ë¦¬ë§ˆì¸ë” ì‚­ì œ ì‹¤íŒ¨:', deleteResult.error);
        }
      }

      set({ 
        isRunning: false, 
        currentReminderId: null 
      });

      await get().saveTimerState();

    } catch (error) {
      console.error('Failed to pause timer:', error);
      await reportError(error, 'TimerPauseError');
      await handleReminderError(error, 'pauseTimer');
    }
  },

  resetTimer: async () => {
    try {
      const { currentReminderId, mode, duration, shortBreak, longBreak } = get();

      if (currentReminderId) {
        const deleteResult = await deleteReminderSafely(currentReminderId);
        if (!deleteResult.success) {
          console.warn('ë¦¬ë§ˆì¸ë” ì‚­ì œ ì‹¤íŒ¨:', deleteResult.error);
        }
      }

      let newDuration;
      switch (mode) {
        case 'focus':
          newDuration = duration;
          break;
        case 'shortBreak':
          newDuration = shortBreak;
          break;
        case 'longBreak':
          newDuration = longBreak;
          break;
        default:
          newDuration = duration;
      }

      set({ 
        isRunning: false, 
        remainingTime: newDuration,
        currentReminderId: null 
      });

      await get().saveTimerState();

    } catch (error) {
      console.error('Failed to reset timer:', error);
      await reportError(error, 'TimerResetError');
      await handleReminderError(error, 'resetTimer');
    }
  },

  completeSession: async () => {
    try {
      const { mode, currentSession, totalSessions, plants, completedPomodoros, totalFocusTime } = get();

      if (mode === 'focus') {
        const newPlant = {
          id: Date.now(),
          type: 'tree',
          completedAt: Date.now(),
          session: currentSession
        };

        const updatedPlants = [...plants, newPlant];
        const newCompletedPomodoros = completedPomodoros + 1;
        const newTotalFocusTime = totalFocusTime + (25 * 60);

        set({
          plants: updatedPlants,
          completedPomodoros: newCompletedPomodoros,
          totalFocusTime: newTotalFocusTime
        });

        try {
          await AppSdk.appData.createData({
            collection: 'plants',
            data: newPlant
          });

          await AppSdk.appData.updateData({
            collection: 'stats',
            id: 'history',
            data: {
              completedPomodoros: newCompletedPomodoros,
              totalFocusTime: newTotalFocusTime
            }
          });
        } catch (storageError) {
          console.error('Failed to save plant data:', storageError);
        }
      }

      let nextMode;
      let nextSession = currentSession;

      if (mode === 'focus') {
        if (currentSession % 4 === 0) {
          nextMode = 'longBreak';
        } else {
          nextMode = 'shortBreak';
        }
        nextSession = currentSession + 1;
      } else {
        nextMode = 'focus';
      }

      if (nextSession > totalSessions) {
        nextSession = 1;
      }

      const { duration, shortBreak, longBreak } = get();
      let newDuration;
      switch (nextMode) {
        case 'focus':
          newDuration = duration;
          break;
        case 'shortBreak':
          newDuration = shortBreak;
          break;
        case 'longBreak':
          newDuration = longBreak;
          break;
        default:
          newDuration = duration;
      }

      set({
        mode: nextMode,
        currentSession: nextSession,
        remainingTime: newDuration,
        isRunning: false,
        currentReminderId: null
      });

      await get().saveTimerState();

    } catch (error) {
      console.error('Failed to complete session:', error);
      await reportError(error, 'SessionCompleteError');
    }
  },

  skipBreak: async () => {
    try {
      const { mode, isRunning } = get();

      // åªå…è®¸åœ¨ä¼‘æ¯æ—¶é—´è·³è¿‡
      if (mode !== 'shortBreak' && mode !== 'longBreak') {
        return;
      }

      console.log('Skipping break...');

      // åœæ­¢å½“å‰è®¡æ—¶å™¨
      if (isRunning) {
        await get().pauseTimer();
      }

      // ç›´æ¥å®Œæˆå½“å‰ä¼šè¯ï¼Œè¿›å…¥ä¸‹ä¸€ä¸ªé˜¶æ®µ
      await get().completeSession();

    } catch (error) {
      console.error('Failed to skip break:', error);
      await reportError(error, 'SkipBreakError');
    }
  },

  updateRemainingTime: (time) => {
    set({ remainingTime: time });
  },

  setMode: (mode) => {
    const { duration, shortBreak, longBreak } = get();
    let newDuration;

    switch (mode) {
      case 'focus':
        newDuration = duration;
        break;
      case 'shortBreak':
        newDuration = shortBreak;
        break;
      case 'longBreak':
        newDuration = longBreak;
        break;
      default:
        newDuration = duration;
    }

    set({ 
      mode, 
      remainingTime: newDuration,
      isRunning: false 
    });
  },

  updateSettings: (settings) => {
    const { mode } = get();
    let newRemainingTime = get().remainingTime;

    if (mode === 'focus' && settings.duration) {
      newRemainingTime = settings.duration;
    } else if (mode === 'shortBreak' && settings.shortBreak) {
      newRemainingTime = settings.shortBreak;
    } else if (mode === 'longBreak' && settings.longBreak) {
      newRemainingTime = settings.longBreak;
    }

    set({ 
      ...settings,
      remainingTime: newRemainingTime
    });
  },

  loadData: async () => {
    try {
      const plantsData = await AppSdk.appData.queryData({
        collection: 'plants',
        query: []
      });

      const statsData = await AppSdk.appData.getData({
        collection: 'stats',
        id: 'history'
      });

      set({
        plants: plantsData || [],
        completedPomodoros: statsData?.completedPomodoros || 0,
        totalFocusTime: statsData?.totalFocusTime || 0
      });

    } catch (error) {
      console.error('Failed to load timer data:', error);
      await reportError(error, 'TimerDataLoadError');
    }
  },

  getTodayStats: () => {
    const { plants, completedPomodoros, totalFocusTime } = get();
    const today = new Date();
    today.setHours(0, 0, 0, 0);
    const todayTime = today.getTime();

    const todayPlants = plants.filter(plant => {
      const plantDate = new Date(plant.completedAt);
      plantDate.setHours(0, 0, 0, 0);
      return plantDate.getTime() === todayTime;
    });

    return {
      todayPomodoros: todayPlants.length,
      todayFocusTime: todayPlants.length * 25 * 60,
      totalPomodoros: completedPomodoros,
      totalFocusTime: totalFocusTime
    };
  },

  debugReminderIssues: async (reminderId) => {
    console.log('=== íƒ€ì´ë¨¸ ìŠ¤í† ì–´ ë¦¬ë§ˆì¸ë” ë””ë²„ê¹… ===');
    await debugReminder(reminderId);

    const { isRunning, mode, remainingTime } = get();
    console.log('í˜„ì¬ íƒ€ì´ë¨¸ ìƒíƒœ:', {
      isRunning,
      mode,
      remainingTime
    });

    console.log('íƒ€ì´ë¨¸ ë¦¬ë§ˆì¸ë” ì •ë¦¬ í…ŒìŠ¤íŠ¸ ì‹œì‘...');
    const cleanupResult = await cleanupTimerRemindersSafely();
    console.log('ì •ë¦¬ ê²°ê³¼:', cleanupResult);

    console.log('=== ë””ë²„ê¹… ì™„ë£Œ ===');
    return cleanupResult;
  },

  // è®¾ç½®æé†’é”™è¯¯å›è°ƒ
  setReminderErrorCallback: (callback) => {
    set({ onReminderError: callback });
  }
}));

styles/FlowerAnimation.module.css:
.animationContainer {
  position: fixed;
  top: 0;
  left: 0;
  width: 100vw;
  height: 100vh;
  pointer-events: none;
  z-index: 1000;
  overflow: hidden;
}

.particle {
  position: absolute;
  font-size: 24px;
  pointer-events: none;
  z-index: 1001;
}

.grow {
  animation: plantGrowth 3s ease-out forwards;
}

.butterfly {
  animation: butterflyFlight 4s ease-in-out forwards;
}

@keyframes plantGrowth {
  0% {
    transform: scale(0) translateY(50px) rotate(0deg);
    opacity: 0;
  }
  20% {
    transform: scale(0.3) translateY(30px) rotate(90deg);
    opacity: 0.6;
  }
  50% {
    transform: scale(0.8) translateY(10px) rotate(180deg);
    opacity: 0.9;
  }
  80% {
    transform: scale(1.2) translateY(-10px) rotate(270deg);
    opacity: 1;
  }
  100% {
    transform: scale(1) translateY(-20px) rotate(360deg);
    opacity: 0;
  }
}

@keyframes butterflyFlight {
  0% {
    transform: translateX(0px) translateY(0px) rotate(0deg) scale(0.5);
    opacity: 0;
  }
  10% {
    opacity: 1;
    transform: scale(1);
  }
  25% {
    transform: translateX(100px) translateY(-50px) rotate(45deg) scale(1.1);
  }
  50% {
    transform: translateX(-50px) translateY(-100px) rotate(-30deg) scale(0.9);
  }
  75% {
    transform: translateX(150px) translateY(-30px) rotate(60deg) scale(1.2);
  }
  90% {
    opacity: 1;
  }
  100% {
    transform: translateX(200px) translateY(-150px) rotate(90deg) scale(0.3);
    opacity: 0;
  }
}

.growthCenter {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  z-index: 1002;
}

.growthRing {
  width: 120px;
  height: 120px;
  border: 3px solid var(--secondary-green);
  border-radius: 50%;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  animation: growthRingExpand 3s ease-out forwards;
}

.growthCore {
  font-size: 48px;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  animation: growthCoreBloom 3s ease-out forwards;
}

@keyframes growthRingExpand {
  0% {
    width: 0px;
    height: 0px;
    opacity: 1;
    border-width: 6px;
  }
  50% {
    width: 200px;
    height: 200px;
    opacity: 0.8;
    border-width: 4px;
  }
  100% {
    width: 300px;
    height: 300px;
    opacity: 0;
    border-width: 1px;
  }
}

@keyframes growthCoreBloom {
  0% {
    transform: translate(-50%, -50%) scale(0) rotate(0deg);
    opacity: 0;
  }
  30% {
    transform: translate(-50%, -50%) scale(1.5) rotate(180deg);
    opacity: 1;
  }
  70% {
    transform: translate(-50%, -50%) scale(1.2) rotate(270deg);
    opacity: 1;
  }
  100% {
    transform: translate(-50%, -50%) scale(1) rotate(360deg);
    opacity: 0;
  }
}

.butterflyTrail {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 300px;
  height: 300px;
  z-index: 1002;
}

.trailDot {
  position: absolute;
  width: 8px;
  height: 8px;
  background: radial-gradient(circle, var(--sky-blue), var(--secondary-green));
  border-radius: 50%;
  animation: trailDotMove 4s ease-in-out infinite;
}

@keyframes trailDotMove {
  0% {
    transform: rotate(0deg) translateX(0px) scale(0);
    opacity: 0;
  }
  10% {
    opacity: 1;
    transform: scale(1);
  }
  25% {
    transform: rotate(90deg) translateX(80px) scale(1.2);
    opacity: 0.8;
  }
  50% {
    transform: rotate(180deg) translateX(120px) scale(0.8);
    opacity: 0.6;
  }
  75% {
    transform: rotate(270deg) translateX(100px) scale(1.1);
    opacity: 0.4;
  }
  90% {
    opacity: 0.2;
  }
  100% {
    transform: rotate(360deg) translateX(0px) scale(0);
    opacity: 0;
  }
}

.trailDot:nth-child(odd) {
  background: radial-gradient(circle, var(--light-green), var(--primary-green));
}

.trailDot:nth-child(3n) {
  background: radial-gradient(circle, var(--earth-brown), var(--secondary-green));
  animation-direction: reverse;
}

.trailDot:nth-child(4n) {
  width: 6px;
  height: 6px;
  background: radial-gradient(circle, var(--sky-blue), transparent);
}

@media (max-width: 768px) {
  .particle {
    font-size: 20px;
  }

  .growthCore {
    font-size: 36px;
  }

  .butterflyTrail {
    width: 200px;
    height: 200px;
  }

  .trailDot {
    width: 6px;
    height: 6px;
  }
}

