styles/Loading.module.css:
.overlay {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(255, 255, 255, 0.95);
  backdrop-filter: blur(8px);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 9999;
  animation: fadeIn 0.3s ease-in-out;
}

.container {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 2rem;
  background: linear-gradient(135deg, #f0f8f0 0%, #e8f5e8 100%);
  border-radius: 20px;
  box-shadow: 0 10px 30px rgba(46, 125, 50, 0.1);
  border: 1px solid rgba(46, 125, 50, 0.1);
  backdrop-filter: blur(10px);
  min-width: 200px;
  text-align: center;
}

.spinner {
  position: relative;
  width: 80px;
  height: 80px;
  margin-bottom: 1.5rem;
  display: flex;
  align-items: center;
  justify-content: center;
}

.leaf {
  position: absolute;
  font-size: 2rem;
  animation: rotate 2s linear infinite;
  transform-origin: center;
}

.leaf:nth-child(1) {
  animation-delay: 0s;
  top: 0;
  left: 50%;
  transform: translateX(-50%);
}

.leaf:nth-child(2) {
  animation-delay: -0.66s;
  top: 50%;
  right: 0;
  transform: translateY(-50%);
}

.leaf:nth-child(3) {
  animation-delay: -1.33s;
  bottom: 0;
  left: 50%;
  transform: translateX(-50%);
}

.message {
  color: #2E7D32;
  font-size: 1rem;
  font-weight: 500;
  margin: 0;
  letter-spacing: 0.5px;
  opacity: 0.9;
}

@keyframes fadeIn {
  from {
    opacity: 0;
    transform: scale(0.95);
  }
  to {
    opacity: 1;
    transform: scale(1);
  }
}

@keyframes rotate {
  0% {
    transform: rotate(0deg) translateX(30px) rotate(0deg);
    opacity: 1;
    scale: 1;
  }
  50% {
    opacity: 0.7;
    scale: 1.2;
  }
  100% {
    transform: rotate(360deg) translateX(30px) rotate(-360deg);
    opacity: 1;
    scale: 1;
  }
}

/* å“åº”å¼è®¾è®¡ */
@media (max-width: 480px) {
  .container {
    padding: 1.5rem;
    min-width: 160px;
  }

  .spinner {
    width: 60px;
    height: 60px;
    margin-bottom: 1rem;
  }

  .leaf {
    font-size: 1.5rem;
  }

  .leaf:nth-child(1),
  .leaf:nth-child(3) {
    transform: translateX(-50%);
  }

  .leaf:nth-child(2) {
    transform: translateY(-50%);
  }

  .message {
    font-size: 0.9rem;
  }

  @keyframes rotate {
    0% {
      transform: rotate(0deg) translateX(20px) rotate(0deg);
      opacity: 1;
      scale: 1;
    }
    50% {
      opacity: 0.7;
      scale: 1.2;
    }
    100% {
      transform: rotate(360deg) translateX(20px) rotate(-360deg);
      opacity: 1;
      scale: 1;
    }
  }
}

