  }

  .taskCard {
    padding: 16px;
  }

  .taskContent {
    gap: 12px;
  }

  .taskActions {
    flex-direction: column;
    gap: 4px;
  }

  .formCard {
    padding: 20px;
  }

  .emptyState {
    padding: 40px 16px;
  }

  .emptyIcon {
    font-size: 48px;
  }
}

