/* ì„ íƒëœ ì‘ì—… í‘œì‹œ - ìƒˆë¡œìš´ ë””ìì¸ */
.selectedTaskDisplay {
  margin-bottom: 24px;
}

.selectedTaskCard {
  background: rgba(255, 255, 255, 0.98);
  backdrop-filter: blur(10px);
  border: 1px solid rgba(200, 230, 201, 0.2);
  border-radius: 20px;
  padding: 20px;
  display: flex;
  align-items: center;
  gap: 16px;
  box-shadow: 
    0 4px 16px rgba(200, 230, 201, 0.08),
