}

.sessionTitle {
  font-size: 24px;
  font-weight: 700;
  color: #2E7D32;
  margin: 0;
  text-shadow: 0 2px 4px rgba(46, 125, 50, 0.1);
}

/* íƒ€ì´ë¨¸ ë””ìŠ¤í”Œë ˆì´ - ìƒˆë¡œìš´ ë””ìì¸ */
.timerDisplay {
  display: flex;
  justify-content: center;
  margin: 32px 0;
}

.timeCircle {
  position: relative;
  width: 220px;
  height: 220px;
  display: flex;
  align-items: center;
  justify-content: center;
}

.progressRing {
  position: absolute;
  width: 100%;
  height: 100%;
  transform: rotate(-90deg);
}

.progressRingBackground {
  stroke-width: 4;
  stroke: rgba(200, 230, 201, 0.3);
}

.progressRingProgress {
  stroke-width: 4;
  stroke-linecap: round;
  transition: stroke-dashoffset 0.5s ease;
  filter: drop-shadow(0 0 8px rgba(46, 125, 50, 0.3));
}

.timeText {
  font-size: 36px;
  font-weight: 700;
  color: #2E7D32;
  text-shadow: 0 2px 8px rgba(46, 125, 50, 0.1);
  background: rgba(255, 255, 255, 0.95);
  backdrop-filter: blur(10px);
  border-radius: 50%;
  width: 160px;
  height: 160px;
  display: flex;
  align-items: center;
  justify-content: center;
  border: 1px solid rgba(200, 230, 201, 0.4);
  box-shadow: 
    0 8px 32px rgba(200, 230, 201, 0.2),
    0 2px 8px rgba(200, 230, 201, 0.1);
}

/* ì»¨íŠ¸ë¡¤ ë²„íŠ¼ - ìƒˆë¡œìš´ ë””ìì¸ */
.controls {
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 12px;
  margin: 32px 0;
  padding: 0 16px;
  flex-wrap: wrap;
}

.playPauseButton,
.resetButton,
.skipButton {
  background: rgba(255, 255, 255, 0.9);
  backdrop-filter: blur(10px);
  border: 1px solid rgba(200, 230, 201, 0.4);
  border-radius: 50px;
  padding: 12px 20px;
  display: flex;
  align-items: center;
  gap: 6px;
  box-shadow: 
    0 8px 32px rgba(200, 230, 201, 0.2),
    0 2px 8px rgba(200, 230, 201, 0.1);
  transition: all 0.3s ease;
  color: #2E7D32;
  font-weight: 600;
  min-width: 100px;
  flex: 0 1 auto;
}

.playPauseButton:hover,
.resetButton:hover,
.skipButton:hover {
  transform: translateY(-2px);
  box-shadow: 
    0 12px 40px rgba(200, 230, 201, 0.25),
    0 4px 12px rgba(200, 230, 201, 0.15);
}

.buttonText {
  font-size: 14px;
  font-weight: 600;
}

/* è·³è¿‡æŒ‰é’®ç‰¹æ®Šæ ·å¼ - è´è¶ä¼‘æ¯æ—¶é—´çš„ç‰¹è‰² */
.skipButton {
  background: linear-gradient(135deg, rgba(160, 200, 230, 0.15), rgba(255, 255, 255, 0.9));
  border: 1px solid rgba(160, 200, 230, 0.4);
  color: #1976D2;
}

.skipButton:hover {
  background: linear-gradient(135deg, rgba(160, 200, 230, 0.25), rgba(255, 255, 255, 0.95));
  border-color: rgba(160, 200, 230, 0.6);
  box-shadow: 
    0 12px 40px rgba(160, 200, 230, 0.3),
    0 4px 12px rgba(160, 200, 230, 0.2);
}

/* í†µê³„ - ìƒˆë¡œìš´ ë””ìì¸ */
.stats {
  display: flex;
  justify-content: center;
  gap: 20px;
  margin: 24px 0;
  flex-wrap: wrap;
}

.statItem {
  display: flex;
  align-items: center;
  gap: 8px;
  background: rgba(255, 255, 255, 0.8);
  backdrop-filter: blur(5px);
  padding: 12px 18px;
  border-radius: 24px;
  border: 1px solid rgba(200, 230, 201, 0.3);
  box-shadow: 
    0 4px 16px rgba(200, 230, 201, 0.15),
    0 1px 4px rgba(200, 230, 201, 0.1);
}

.statIcon {
  font-size: 18px;
}

.statText {
  font-size: 14px;
  font-weight: 600;
  color: #2E7D32;
}

/* ì •ì› ìš”ì†Œë“¤ - ë¯¸ë‹ˆë©€í•˜ê²Œ ìˆ˜ì • */
.gardenElements {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  pointer-events: none;
  overflow: hidden;
}

.backgroundTrees {
  position: absolute;
  width: 100%;
  height: 100%;
}

.tree {
  position: absolute;
  font-size: 20px;
  animation: gentle-sway 6s ease-in-out infinite;
  opacity: 0.3;
  filter: blur(0.5px);
}

@keyframes gentle-sway {
  0%, 100% { 
    transform: rotate(0deg);
  }
  25% { 
    transform: rotate(1deg);
  }
  75% { 
    transform: rotate(-1deg);
  }
}

.growingPlants {
  position: absolute;
  bottom: 25%;
  left: 50%;
  transform: translateX(-50%);
}

.plantGrowth {
  display: flex;
  gap: 12px;
}

.growingPlant {
  font-size: 18px;
  opacity: 0.6;
  animation: plant-grow 4s ease-in-out infinite;
}

@keyframes plant-grow {
  0% { 
    transform: scale(0.9) translateY(5px);
    opacity: 0.4;
  }
  50% { 
    transform: scale(1.05) translateY(-3px);
    opacity: 0.8;
  }
  100% { 
    transform: scale(1) translateY(0px);
    opacity: 0.6;
  }
}

.flowers {
  position: absolute;
  width: 100%;
  height: 100%;
}

.flower {
  position: absolute;
  font-size: 14px;
  opacity: 0.7;
  animation: flower-bloom 3s ease-out;
}

@keyframes flower-bloom {
  0% {
    transform: scale(0) rotate(0deg);
    opacity: 0;
  }
  50% {
    transform: scale(1.2) rotate(180deg);
    opacity: 0.8;
  }
  100% {
    transform: scale(1) rotate(360deg);
    opacity: 0.7;
  }
}

.butterflies {
  position: absolute;
  width: 100%;
  height: 100%;
}

.butterfly {
  position: absolute;
  font-size: 16px;
  opacity: 0.6;
  animation: butterfly-fly 12s ease-in-out infinite;
}

@keyframes butterfly-fly {
  0% {
    left: -5%;
    top: 30%;
    transform: rotate(0deg);
  }
  25% {
    left: 30%;
    top: 20%;
    transform: rotate(5deg);
  }
  50% {
    left: 70%;
    top: 40%;
    transform: rotate(-3deg);
  }
  75% {
    left: 90%;
    top: 25%;
    transform: rotate(8deg);
  }
  100% {
    left: 105%;
    top: 35%;
    transform: rotate(0deg);
  }
}

.clouds {
  position: absolute;
  width: 100%;
  height: 100%;
}

.cloud {
  position: absolute;
  font-size: 18px;
  animation: cloud-drift 20s linear infinite;
  opacity: 0.4;
  filter: blur(0.5px);
}

@keyframes cloud-drift {
  0% {
    transform: translateX(-5px);
  }
  100% {
    transform: translateX(5px);
  }
}

/* ë°˜ì‘í˜• ë””ìì¸ */
@media (max-width: 768px) {
  .container {
    padding: 12px;
  }

  .taskSelection {
    padding: 20px;
    margin-bottom: 20px;
  }

  .taskSelectionTitle {
    font-size: 16px;
  }

  .selectedTaskCard {
    padding: 16px;
  }

  .selectedTaskIcon {
    font-size: 24px;
  }

  .selectedTaskText {
    font-size: 14px;
  }

  .timeCircle {
    width: 180px;
    height: 180px;
  }

  .timeText {
    font-size: 28px;
    width: 130px;
    height: 130px;
  }

  .sessionTitle {
    font-size: 20px;
  }

  .controls {
    gap: 8px;
    padding: 0 12px;
  }

  .playPauseButton,
  .resetButton,
  .skipButton {
    padding: 10px 16px;
    min-width: 80px;
  }

  .buttonText {
    font-size: 12px;
  }

  .stats {
    gap: 16px;
  }

  .statItem {
    padding: 10px 16px;
  }

  .statText {
    font-size: 12px;
  }
}

styles/Welcome.module.css:
/* Welcome ç»„ä»¶æ ·å¼ - ç®€çº¦é£æ ¼ */

.welcomeContainer {
  background: #ffffff;
  min-height: 100vh;
  color: #333333;
  position: relative;
  padding: 0;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
}

/* å¤´éƒ¨ */
.header {
  text-align: center;
  padding: 56px 16px 16px;
}

.logo {
  display: inline-block;
  margin-bottom: 12px;
}

.logoImage {
  width: 64px;
  height: 64px;
  border-radius: 12px;
  transition: opacity 0.2s ease;
  box-shadow: 0 0 10px 0 rgba(0, 0, 0, 0.1);
}

.logoImage:hover {
  opacity: 0.8;
}

.title {
  font-size: 2.1rem;
  font-weight: 600;
  margin: 0 0 6px 0;
  line-height: 1.2;
  letter-spacing: -0.02em;
}

.titleGradient {
  color: #1a1a1a;
}

.titleSubtext {
  color: #666666;
  font-weight: 400;
  margin-left: 6px;
}

.subtitle {
  font-size: 1rem;
  color: #666666;
  margin: 0;
  font-weight: 400;
}

/* æ­¥éª¤ */
.steps {
  display: flex;
  flex-direction: column;
  gap: 8px;
  padding: 8px 16px 16px;
  max-width: 560px;
  margin: 0 auto;
}

.step {
  padding: 12px 12px;
  background: #fff;
  border-radius: 12px;
  border: 1px solid rgba(0, 0, 0, 0.1);
  position: relative;
  text-align: center;
  transition: background-color 0.2s ease;
}

.stepContent {
  margin-top: 0;
}

.stepContent h3 {
  font-size: 1.1rem;
  font-weight: 500;
  color: #1a1a1a;
  margin: 0 0 4px 0;
}

.stepContent p {
  color: #666666;
  margin: 0;
  font-size: 0.95rem;
  line-height: 1.4;
}

.stepNumber {
  position: absolute;
  top: 8px;
  left: 8px;
  background: #e5e5e5;
  color: #666666;
  width: 20px;
  height: 20px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  font-weight: 500;
  font-size: 0.75rem;
}

/* è¡ŒåŠ¨æŒ‰é’® */
.ctaSection {
  text-align: center;
  padding: 16px 16px;
  display: flex;
  flex-direction: column;
  gap: 8px;
  align-items: center;
}

.primaryBtn {
  background: #1a1a1a;
  color: #ffffff;
  border: none;
  border-radius: 6px;
  padding: 10px 18px;
  font-weight: 500;
  font-size: 0.95rem;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 8px;
  justify-content: center;
  transition: background-color 0.2s ease;
  width: 180px;
  max-width: 90%;
}

.primaryBtn:hover {
  background: #333333;
}

.primaryBtn:active {
  background: #000000;
}

.secondaryBtn {
  background: transparent;
  color: #666666;
  border: 1px solid #d5d5d5;
  border-radius: 6px;
  padding: 10px 18px;
  font-weight: 500;
  font-size: 0.95rem;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 8px;
  justify-content: center;
  transition: all 0.2s ease;
  width: 180px;
  max-width: 90%;
}

.secondaryBtn:hover {
  background: #fafafa;
  border-color: #999999;
}

/* åº•éƒ¨ */
.footer {
  text-align: center;
  padding: 8px 16px 16px;
  color: #999999;
  font-size: 0.85rem;
}

.footer p {
  margin: 0;
}

/* å“åº”å¼è®¾è®¡ */
@media (max-width: 768px) {
  .header {
    padding: 40px 12px 16px;
  }

  .title {
    font-size: 2rem;
  }

  .subtitle {
    font-size: 1rem;
  }

  .steps {
    padding: 8px 12px 16px;
  }

  .step {
    padding: 12px 12px;
  }

  .ctaSection {
    padding: 16px 12px;
  }
}

@media (max-width: 480px) {
  .header {
    padding: 32px 12px 14px;
  }

  .logoImage {
    width: 52px;
    height: 52px;
  }

  .title {
    font-size: 1.7rem;
  }

  .primaryBtn, .secondaryBtn {
    width: 100%;
    max-width: 280px;
  }
}

/* æ¡Œé¢ç«¯æ›´ç´§å‡‘çš„æ­¥éª¤å¸ƒå±€ */
@media (min-width: 768px) {
  .steps {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    gap: 10px;
    max-width: 840px;
  }
  .step {
    text-align: left;
    padding: 12px;
  }
  .stepNumber {
    top: 10px;
    left: 10px;
  }
}

/* ç®€åŒ–åŠ¨ç”» */
@keyframes fadeIn {
  from {
    opacity: 0;
    transform: translateY(12px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

/* åŸºç¡€è¿›åœºåŠ¨ç”» */
.logo,
.title,
.subtitle,
.step,
.ctaSection {
  opacity: 0;
  animation: fadeIn 0.5s ease-out forwards;
}

.logo {
  animation-delay: 0s;
}

.title {
  animation-delay: 0.1s;
}

.subtitle {
  animation-delay: 0.2s;
}

.step:nth-child(1) { 
  animation-delay: 0.3s;
}

.step:nth-child(2) { 
  animation-delay: 0.4s;
}

.step:nth-child(3) { 
  animation-delay: 0.5s;
}

.ctaSection {
  animation-delay: 0.6s;
}

styles/global.css:
:root {
  --primary-green: #2E7D32;
  --secondary-green: #7FB069;
  --light-green: #BED8A3;
  --earth-brown: #8B6B4A;
  --sky-blue: #A0C8E6;
  --cream-white: #FFF8F0;
  --soft-gray: #F5F5F5;
  --text-dark: #2D3748;
  --text-light: #718096;
  --shadow-color: rgba(46, 125, 50, 0.1);
  --border-color: rgba(46, 125, 50, 0.2);

  /* æŒ‰é’®ä¸»é¢˜å˜é‡ */
  --button-primary-bg: linear-gradient(135deg, var(--secondary-green), var(--primary-green));
  --button-primary-hover-bg: linear-gradient(135deg, #8FC470, #357A39);
  --button-primary-active-bg: linear-gradient(135deg, #6FA05B, #1E5F22);
  --button-primary-shadow: rgba(127, 176, 105, 0.3);
  --button-primary-hover-shadow: rgba(127, 176, 105, 0.4);
  --button-primary-active-shadow: rgba(127, 176, 105, 0.5);
  --button-secondary-color: var(--secondary-green);
  --button-secondary-hover-bg: rgba(127, 176, 105, 0.1);
  --button-secondary-active-bg: rgba(127, 176, 105, 0.2);
}

* {
  box-sizing: border-box;
}

body {
  margin: 0;
  padding: 0;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', 'Oxygen', 'Ubuntu', 'Cantarell', sans-serif;
  background: #FAFCFA;
  color: var(--text-dark);
  min-height: 100vh;
}

/* å¼ºåˆ¶ Light ä¸»é¢˜ */
html {
  --ion-color-base: #000000;
  --ion-color-contrast: #ffffff;
  --ion-background-color: #ffffff;
  --ion-background-color-rgb: 255,255,255;
  --ion-text-color: #000000;
  --ion-text-color-rgb: 0,0,0;
  --ion-color-step-50: #f2f2f2;
  --ion-color-step-100: #e6e6e6;
  --ion-color-step-150: #d9d9d9;
  --ion-color-step-200: #cccccc;
  --ion-color-step-250: #bfbfbf;
  --ion-color-step-300: #b3b3b3;
  --ion-color-step-350: #a6a6a6;
  --ion-color-step-400: #999999;
  --ion-color-step-450: #8c8c8c;
  --ion-color-step-500: #808080;
  --ion-color-step-550: #737373;
  --ion-color-step-600: #666666;
  --ion-color-step-650: #595959;
  --ion-color-step-700: #4d4d4d;
  --ion-color-step-750: #404040;
  --ion-color-step-800: #333333;
  --ion-color-step-850: #262626;
  --ion-color-step-900: #1a1a1a;
  --ion-color-step-950: #0d0d0d;
}

/* å¼ºåˆ¶æ‰€æœ‰ Ionic ç»„ä»¶ä½¿ç”¨ light æ¨¡å¼ */
.ion-palette-light,
.ion-palette-light ion-app,
.ion-palette-light ion-page,
.ion-palette-light ion-content {
  --ion-color-base: #000000 !important;
  --ion-color-contrast: #ffffff !important;
  --ion-background-color: #ffffff !important;
  --ion-background-color-rgb: 255,255,255 !important;
  --ion-text-color: #000000 !important;
  --ion-text-color-rgb: 0,0,0 !important;
}

.garden-card {
  background: var(--cream-white);
  border-radius: 16px;
  padding: 20px;
  margin: 16px;
  box-shadow: 0 4px 20px var(--shadow-color);
  border: 1px solid var(--border-color);
  position: relative;
  overflow: hidden;
}

.garden-card::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  height: 4px;
  background: linear-gradient(90deg, var(--primary-green), var(--secondary-green), var(--light-green));
  border-radius: 16px 16px 0 0;
}

.garden-button {
  --background: var(--primary-green);
  --background-hover: var(--secondary-green);
  --background-activated: var(--primary-green);
  --color: white;
  --border-radius: 12px;
  --padding-start: 20px;
  --padding-end: 20px;
  --padding-top: 12px;
  --padding-bottom: 12px;
  font-weight: 600;
  text-transform: none;
  box-shadow: 0 4px 12px rgba(46, 125, 50, 0.3);
  position: relative;
  overflow: hidden;
}

.garden-button::before {
  content: '';
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 100%;
  background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.2), transparent);
  transition: left 0.5s;
}

.garden-button:hover::before {
  left: 100%;
}

.garden-button.secondary {
  --background: var(--earth-brown);
  --background-hover: #A0845A;
  --background-activated: var(--earth-brown);
}

.garden-button.tertiary {
  --background: var(--sky-blue);
  --background-hover: #B8D4EA;
  --background-activated: var(--sky-blue);
  --color: var(--text-dark);
}

.gradient-text {
  background: linear-gradient(135deg, var(--primary-green), var(--secondary-green));
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  font-weight: 700;
  margin: 0 0 16px 0;
}

.timer-circle {
  width: 280px;
  height: 280px;
  border-radius: 50%;
  background: var(--cream-white);
  border: 8px solid var(--light-green);
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  margin: 32px auto;
  position: relative;
  box-shadow: 
    0 8px 32px rgba(46, 125, 50, 0.2),
    inset 0 4px 8px rgba(255, 255, 255, 0.8);
  overflow: hidden;
}

.timer-circle::before {
  content: '';
  position: absolute;
  top: -4px;
  left: -4px;
  right: -4px;
  bottom: -4px;
  border-radius: 50%;
  background: linear-gradient(45deg, var(--primary-green), var(--secondary-green), var(--light-green), var(--sky-blue));
  z-index: -1;
  animation: rotate 20s linear infinite;
}

@keyframes rotate {
  from { transform: rotate(0deg); }
  to { transform: rotate(360deg); }
}

.timer-progress {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  border-radius: 50%;
  transition: background 0.3s ease;
}

.timer-time {
  font-size: 48px;
  font-weight: 700;
  color: var(--primary-green);
  text-shadow: 0 2px 4px rgba(46, 125, 50, 0.1);
  margin-bottom: 8px;
}

.timer-label {
  font-size: 16px;
  color: var(--text-light);
  font-weight: 500;
}

.pulse-glow {
  animation: pulseGlow 2s ease-in-out infinite;
}

@keyframes pulseGlow {
  0%, 100% {
    box-shadow: 
      0 8px 32px rgba(46, 125, 50, 0.2),
      inset 0 4px 8px rgba(255, 255, 255, 0.8);
  }
  50% {
    box-shadow: 
      0 12px 40px rgba(46, 125, 50, 0.4),
      inset 0 4px 8px rgba(255, 255, 255, 0.8),
      0 0 20px rgba(127, 176, 105, 0.6);
  }
}

.plant-decoration {
  position: absolute;
  font-size: 24px;
  animation: float 3s ease-in-out infinite;
  pointer-events: none;
}

.plant-decoration:nth-child(1) {
  top: 20px;
  right: 20px;
  animation-delay: 0s;
}

.plant-decoration:nth-child(2) {
  bottom: 20px;
  left: 20px;
  animation-delay: 0.5s;
}

.plant-decoration:nth-child(3) {
  top: 50%;
  right: 10px;
  animation-delay: 1s;
}

.plant-decoration:nth-child(4) {
  top: 50%;
  left: 10px;
  animation-delay: 1.5s;
}

@keyframes float {
  0%, 100% {
    transform: translateY(0px) rotate(0deg);
  }
  33% {
    transform: translateY(-10px) rotate(5deg);
  }
  66% {
    transform: translateY(5px) rotate(-3deg);
  }
}

.grow-animation {
  animation: grow 0.8s ease-out;
}

@keyframes grow {
  0% {
    transform: scale(0) rotate(0deg);
    opacity: 0;
  }
  50% {
    transform: scale(1.2) rotate(180deg);
    opacity: 0.8;
  }
  100% {
    transform: scale(1) rotate(360deg);
    opacity: 1;
  }
}

.butterfly-float {
  animation: butterflyFloat 4s ease-in-out infinite;
}

@keyframes butterflyFloat {
  0%, 100% {
    transform: translateX(0px) translateY(0px) rotate(0deg);
  }
  25% {
    transform: translateX(20px) translateY(-15px) rotate(5deg);
  }
  50% {
    transform: translateX(-10px) translateY(-25px) rotate(-3deg);
  }
  75% {
    transform: translateX(-20px) translateY(-10px) rotate(2deg);
  }
}

.garden-tab-bar {
  --background: var(--cream-white);
  --border: 1px solid var(--border-color);
